css:
@-moz-document domain("clickup.com") {
	
	.gantt_task .gantt_task_scale .gantt_scale_cell.today-active {
		border: 0px solid!important;
		color: #fff !important;
		background-color: #068eef !important;
	}	
	
	div[style*="background-color: rgb(213, 213, 214);"] .gantt_task_progress {
		background-color: #828282 !important;
	}
	div[style*="background-color: rgb(213, 213, 214);"] {
		background-color: #aaa !important;
		border-radius: 4px 4px 0 0 !important;
	}
	div[style*="background-color: rgb(213, 213, 214);"] .gantt-bar-text-dark {
		color: #fff !important;
	}

	div[style*="background-color: rgb(251, 228, 77);"] .gantt_task_progress {
		background-color: #e8bd1d !important;
		border-radius: 4px 4px 0 0 !important;
	}
	div[style*="background-color: rgb(251, 228, 77);"] {
		background-color: #f7d142 !important;
		border-radius: 4px 4px 0 0 !important;
	}
	div[style*="background-color: rgb(251, 228, 77);"] .gantt-bar-text-dark {
		color: #604800 !important;
	}

	div[style*="background-color: rgb(79, 195, 247);"] .gantt_task_progress {
		background-color: #0a8eef !important;
	}
	div[style*="background-color: rgb(79, 195, 247);"] {
		background-color: #04a8f4 !important;
		border-radius: 4px 4px 0 0 !important;
	}
	
	div[style*="background-color: rgb(109, 220, 154);"] .gantt_task_progress {
		background-color: #088d00 !important;
		border-radius: 4px 4px 0 0 !important;
	}
	div[style*="background-color: rgb(109, 220, 154);"] {
		background-color: #2dc324 !important;
		border-radius: 4px 4px 0 0 !important;
	}
	div[style*="background-color: rgb(192, 240, 212);"] {
		background-color: #dbf3b6 !important;
	}

	.gantt_task_line.project-bar .gantt-bar-text-dark,
	.list-bar .gantt_task_content p.gantt-bar-text-dark {
		color: #fff;
	}
	
	.gantt {
		height: 100%;
		transform: inherit;	
	}
	
	.gantt__date-scale,
	.cu2-views-bar__selected-view-options-container,
	.cu2-views-bar__options {
		display: none;
	}


}
