@media (prefers-color-scheme: light) {
    :root {
        --background: #FAFAFA;
        --background-transparent: rgba(250, 250, 250, 0.5);
        --object-background: #F5F5F5;
        --object-hover: #EEEEEE;
        --object-hover-hover: #E0E0E0;

        --link: #212121;
        --link-visited: #757575;

        --text: #212121;
        --faded-text: #616161;

        --search-box-shadow: 0 0 0 1px rgba(0, 0, 0, .1), 0 2px 4px 0 rgba(0, 0, 0, .16);
        --search-box-shadow-hover: 0 0 0 1px rgba(0, 0, 0, .1), 0 2px 4px 0 rgba(0, 0, 0, .18);
        --shadow-border: #C8C8C8;

        --border: #DDDDDD;

        --accent: /*[[lacc]]*/;

        --light-to-dark: invert(1);
        --dark-to-light: none;
    }
}
@media (prefers-color-scheme: dark) {
    :root {
        --background: #212121;
        --background-transparent: rgba(33, 33, 33, 0.5);
        --object-background: #333333;
        --object-hover: #424242;
        --object-hover-hover: #616161;

        --link: #FAFAFA;
        --link-visited: #9E9E9E;

        --text: #FAFAFA;
        --faded-text: #BDBDBD;

        --search-box-shadow: 0 0 0 1px rgb(0, 0, 0), 0 2px 4px 0 rgba(0, 0, 0, 0.5);
        --search-box-shadow-hover: 0 0 0 1px rgb(0, 0, 0), 0 2px 4px 0 rgb(0, 0, 0);
        --shadow-border: #000000;

        --border: #424242;

        --accent: /*[[dacc]]*/;

        --light-to-dark: none;
        --dark-to-light: invert(1);
    }
}

