css:
@-moz-document domain('ntnu.1024.no') {
	


* {
    color: #999;
}
.wrapper * {
    color: #222 !important;
}
.odd {
    background-color: #999;
}
html, body {
    background-color: #333;
}
#courses tbody tr td, #lectures tbody tr td {
    color: #000;
}
.time .wrapper {
    color: #111;
}
#courses tbody tr td a, #lectures tbody tr td a {
    color: #000;
}
a {
    color: #CCC;
}
.icon-remove, .icon-wrench, .icon-list-alt, .icon-question-sign, .icon-download-alt, .icon-globe, .icon-facebook-sign, .icon-twitter-sign, .icon-plus-sign, .icon-list, .icon-link, .icon-save, .icon-ban-circle, .icon-pencil, .icon-calendar, .icon-external-link {
    filter: invert(100%);
}
button {
    padding: 3px;
    background-color: #777;
    color: #CCC;
    border: 2px black;
}
tbody {
    filter: invert(100%)
}
input[type="checkbox"] {
    filter: invert(73%);
}
input {
    filter: invert(0);
    background-color: #777;
    color: #333;
    border: 1px black;
}
}
