css:
@-moz-document domain(moodle.rwth-aachen.de) {

.list-group-item {
    color: #FFF;
    background-color: #06C;
    padding: 5px 10px;
    margin: 2px 0 0 0;
    display: block;
    text-align: left;
    font: bold 1em;
    border-width: 0;
    text-decoration: none;
}
.list-group-item :hover {
    background-color: #2EF;
    color: #000;
    margin-right: -10px;
    padding-right: 10px;
    margin-left: -15px;
    padding-left: 10px;
    margin-top: -5px;
    padding-top: 5px;
    margin-bottom: -5px;
    padding-bottom: 5px;
}
.list-group-item[href*="#section-"] :hover {
    margin-left: -3.0rem;
    padding-left: 2.0rem;
}
.list-group-item[data-key^="localboostnavigationactivity"] :hover {
    margin-left: -3.0rem;
    padding-left: 2.0rem;
}
.list-group-item[data-parent-key="mycourses"] :hover {
    margin-left: -3.0rem;
    padding-left: 2.0rem;
}
.list-group-item[data-key="coursehome"] :hover {
    margin-left: -1.9rem;
    padding-left: 1.3rem;
    margin-right: -1.2rem;
    padding-right: 1.5rem;
    margin-top: -1.0rem;
    padding-top: 1.0rem;
    margin-bottom: -0.5rem;
    padding-bottom: 1.2rem;
}
.sectionname {
    font-size: 14px;
    background-color: #06C;
    margin: -20px -20px 0em;
    padding: 5px 30px 20px 30px !important;
    text-align: left;
    color: white;
    font-weight: bold;
    font-size: 14px;
    height: 14px;
    vertical-align: middle;
    white-space: nowrap;
}
.sectionname > span > a {
    color: white;
}
#region-main {
    background-color: #2EF;
    font-size: 14px;
}
.card-title {
    font-size: 14px;
    background-color: #F22;
    margin: -1.0rem -1.0rem -1.0rem -1.0rem !important;
    padding: 5px 30px 5px 20px !important;
    text-align: left;
    color: white;
    font-weight: bold;
    font-size: 14px;
    height: 14px;
    vertical-align: middle;
    white-space: nowrap;
}
.card-title:before {
    content:'';
    display: inline-block;
    vertical-align:middle;
    width:110%;
    height:27px;
    margin-top: -0.25rem;
    background-color: #F22;
    margin-left: -1.4rem;
    margin-right: -100%;
}
.block.card {
    background-color: #FFC;
    margin: 20px 10px 0px 10px;
    font-size: 14px;
    padding: 0 0 0 0;
}
.card-body.p-3 {
    padding-top: 0 !important;
}

}
