css:
@-moz-document domain("goodblox.xyz") {
body {
    font: normal 8pt/normal Verdana,sans-serif;
    margin: 0;
    text-transform: none;
    text-decoration: none;
    background: url(http://web.archive.org/web/20100522111914im_/http://www.roblox.com/images/bg_clouds.jpg) no-repeat scroll center top, #F8FCFF url(http://web.archive.org/web/20100522112159im_/http://www.roblox.com/images/bg_gradient.jpg) repeat-x;
}
#Banner {
    background-image: url(http://web.archive.org/web/20100706162816im_/http://www.roblox.com/images/WoodBanner.png);
    background-repeat: no-repeat;
    height: 72px;
    text-align: center;
    border: none;
}
#Header {
    border-top: none;
}
#Container {
    margin: 0 auto;
    padding: 0 10px;
    border-left: 5px solid #DDD;
    border-right: 5px solid #DDD;
    width: 900px;
    background: #e5f1fd;
}
#Header .Navigation {
    background-color: #6e99c9;
    border: none;
    font-family: Arial,Helvetica,sans-serif;
}
#Options div {
    background: #6E99C9;
}
#Logo img {
    visibility: hidden;
}
#Banner #Alerts #AlertSpace {
    border:1px solid #6E99C9;
    background:#FFF;
    width:203px;
    position: inherit;
    height: 62px;
    margin-top: -4px;
    margin-right: 63px;
}
.tablehead {
    background-color: #6e99c9!important;
    text-align: center!important;
    color: White!important;
    font-size: 16px;
    font-weight: bold;
    border-bottom: none!important;
    padding: 5px 10px 5px 10px;
}
.GridHeader {
    color: White;
    background-color: #6E99C9;
    height: 34px;
}
.Grid {
    width: 900px;
}
.GridPager {
    background-color: #6E99C9;
}
#Error h2 {
    background-color: #6e99c9!important;
    text-align: center!important;
    color: White!important;
    font-size: 16px;
    font-weight: bold;
    border-bottom: none!important;
    padding: 5px 10px 5px 10px;
}
#Error {
    border: 2px solid #6e99c9;
    background-color: White;
}
#Banner .SignUpAndPlay img {
    content: url("http://web.archive.org/web/20100801163809im_/http://www.roblox.com/images/Holiday3Button.png")
}
}

@-moz-document url-prefix("https://goodblox.xyz/catalog/") {
#CatalogContainer .DisplayFilters {
    border: 2px solid #6e99c9;
    background-color: White!important;
    width: 183px;
}
#CatalogContainer .SearchBar {
    border: 2px solid #6e99c9;
    height: 38px;
    clear: both;
    display: block;
    background-color: #006699;
    margin-top: -10px;
}
.DisplayFilters #Category, .DisplayFilters #BrowseMode, #CatalogContainer h2, .DisplayFilters div {
    padding-left: 10px;
}
#CatalogContainer h2 {
    padding-top: 20px;
}
#CatalogContainer .AssetsDisplaySet {
    float: inherit;
    background-color: #6e99c9;
    text-align: center;
    color: White;
    font-size: 16px;
    font-weight: bold!important;
    padding: 5px 10px 5px 10px;
    display: block;
    width: 690px;
    font: normal 12pt/normal Verdana,sans-serif;
}
#CatalogContainer .Assets {
    float: inherit;
    border: 2px solid #6e99c9;
    background-color: White;
    display: block;
    width: 700px;
}
#CatalogContainer .Asset .AssetThumbnail {
    border: solid 1px #EEE;
}
#CatalogContainer .SearchBar .TextBox {
    width: 520px;
    margin-top: 7px;
    border: solid 1px #EEE;
}
td {
    width: 130px;
}
}

@-moz-document url("https://goodblox.xyz/") {
#LoginView h5 {
font-size: 14px;
    font-weight: bold;
    text-align: center;
    padding-top: 5px;
    padding-bottom: 0;
    background-color: transparent;
    border-bottom: solid 2px #6e99c9;
}
#LoginView {
    background: url(http://web.archive.org/web/20100706162736id_/http://www.roblox.com/images/DarkGradientW150.png) repeat-y top left;
    width: 150px;
    color: White;
    position: relative;
    background-color: transparent;
    border: solid 2px #7DADE0;
}
#LoginView .AspNet-Login, #LoginView #AlreadySignedIn {
    background-color: transparent;
    color: White;
}
#SplashContainer #GoodBloxAtAGlance {
    border: 2px solid #6e99c9;
    background-color: White!important;
}
.Button {
cursor: pointer;
    background-color: #435D77;
    color: White;
    text-decoration: none;
    border: solid 1px #777;
    padding: 6px 17px 6px 17px;
    font-family: Verdana,Sans-Serif;
    font-size: 12px;
    font-weight: bold;
    text-align: center;
    white-space: nowrap;
}
.Button:hover {
    background-color: #B6CCE4;
    color: #435D77;
    border: solid 1px #777;
}
}

@-moz-document url-prefix("https://goodblox.xyz/users/?id="), url-prefix("https://goodblox.xyz/my/home/") {
.Title, #Friends h4, #UserAssets h4, #UserBadges h4, #UserStatistics h4, #Favorites h4 {
    float: inherit;
    background-color: #6e99c9!important;
    text-align: center!important;
    color: White!important;
    font-size: 16px;
    font-weight: bold;
    border-bottom: none!important;
    padding: 5px 10px 5px 10px;
    display: block;
    font-family: Verdana,Sans-Serif!important;
}
#UserStatisticsPane, #UserBadgesPane, #FriendsPane, #ProfilePane, #UserAssetsPane, #UserPlacesPane, #FavoritesPane {
    float: inherit;
    border: 2px solid #6e99c9!important;
    background-color: White!important;
    margin-bottom: 10px;
    display: block;
    border-right: 2px solid #6e99c9;
}
#UserStatistics {
    background-color: White!important;
}
#ProfilePane table {
     background-color: White!important;
    width: 440px;
}
#ProfilePane td {
     padding: 0px;
}
#Friends h4 {
    margin: 0px;
}
#UserPlaces {
    padding: 10px;
}
#UserPlaces .AccordionHeader {
    background-color: #ccc;
    border: solid 1px #000;
}
#Friends h4 a,#UserBadges h4 a {
    color: yellow;
}
.PanelToggle {
    display: none;
    cursor: auto;
}
#Favorites {
    width: 440px;
}
}

@-moz-document url-prefix("https://goodblox.xyz/friends/?of=") {
#Friends h4 {
    background-color: #6e99c9!important;
    text-align: center!important;
    color: White!important;
    font-size: 16px;
    font-weight: bold;
    padding: 5px 10px 5px 10px;
}
#Friends table {
    border: 2px solid #6e99c9;
    background-color: White!important;
    border-right: 2px solid #6e99c9;
    width: 900px;
    top: -8px;
    position: relative;
}
}

@-moz-document url-prefix("https://goodblox.xyz/forum/") {
th {
    background-color: #6e99c9;
    background-image: none;
}
td.forumHeaderBackgroundAlternate {
    background-image: none;
    background-color: #BDD6F5;
}
td.forumRowHighlight {
    background-color: #dfe9fc;
}
.CenterColumn a {
    color: #0000FF;
}
}

@-moz-document url-prefix("https://goodblox.xyz/catalog/item?id=") {
#ItemContainer h2 {
    float: inherit;
    background-color: #6e99c9!important;
    text-align: center;
    color: White;
    font-size: 16px;
    font-weight: bold;
    padding: 5px 10px 5px 10px;
    display: block;
    border: none;
}
#ItemContainer {
    border: 2px solid #6e99c9;
    background-color: White;
    margin-bottom: 10px;
    display: block;
}
}

@-moz-document url-prefix("https://goodblox.xyz/my/character/") {
.fixed {
    right: 2px!important;
    padding: 2px!important;
    top: 2px!important;
}
.fixed:hover {
    text-decoration: underline!important;
}
.margin {
    margin: unset;
}
#Body table {
    border: 2px solid #6e99c9;
    background-color: White!important;
    margin-bottom: 10px;
}
}

@-moz-document url-prefix("https://goodblox.xyz/badges/") {
#BadgesContainer .Legend .BadgesList li {
    margin: 10px 0 20px 0;
    padding: 0 0 7px 80px;
    border: solid 1px #000;
}
#FeaturedBadge_Community {
    background-color: White;
}
#BadgesContainer #CommunityBadges .Legend #Administrator {
    background-image: url(http://web.archive.org/web/20100706162842im_/http://www.roblox.com/images/Badges/Administrator-75x75.png?v=2);
}
}

@-moz-document url-prefix("https://goodblox.xyz/catalog/item?id=") {
#ItemContainer #Thumbnail, #Thumbnail img {
    height: 420px!important;
    width: 420px!important;
}
#ItemContainer #Thumbnail {
	border: solid 1px #eee;
}
#ItemContainer #Summary {
    background-color:  #fff;
    border: dashed 1px #555;
    padding: 7px;
    width: 235px;
}
#ItemContainer h2 {
    width: 700px;
    margin-bottom: 10px;
}
#ItemContainer #Summary #Description {
    width: 220px;
}
#ItemContainer #Actions {
    width: 410px!important;
}
#ItemContainer #Summary h3 {
    margin-left: -1px;
}
.ReportAbusePanel {
    text-align: center!important;
}
#ItemContainer #Configuration, #ItemContainer #Ownership {
    width: 229px;
}
}

@-moz-document url-prefix("https://goodblox.xyz/games") {
#GamesContainer .DisplayFilters {
    float: inherit;
    border: 2px solid #6e99c9;
    background-color:White;
    margin-bottom: 10px;
    display: block;
    width: 126px;
}
#GamesContainer h2,#GamesContainer .GamesDisplaySet {
    float: inherit;
    background-color:#6e99c9;
    text-align: center;
    color:White;
    font-size: 16px;
    font-weight: bold;
    padding: 5px 10px 5px 10px;
    display: block;
    font-family: Verdana,Sans-Serif;
}
#GamesContainer #Games {
    margin-left: 140px;
    margin-top: -325px;
}
#BrowseMode, #Timespan {
	margin-left: 10px;
}
#GamesContainer #Games {
    float: inherit;
    border: 2px solid #6e99c9;
    background-color:White;
    width: 590px!important;
    margin-bottom: 10px;
    display: block;
}
}

@-moz-document url-prefix("https://goodblox.xyz/games/view?id=") {
#ItemContainer h2 {
	float: inherit;
	background-color: #6e99c9;
	text-align: center;
	color: White;
	font-size: 16px;
	font-weight: bold;
	padding: 5px 10px 5px 10px;
	display: block;
    border-bottom: none;
    width: 707px;
    margin-bottom: 10px;
}
#ItemContainer {
	border: 2px solid #6e99c9;
	background-color: White;
	margin-bottom: 10px;
	display: block;
}
#ItemContainer #Thumbnail_Place {
    margin-bottom: 10px;
}
#ItemContainer #Thumbnail_Place img {
    border: solid 1px #eee;
}
}

@-moz-document url-prefix("https://goodblox.xyz/my/character/upload/?type=") {
#Body {
    	border: 2px solid #6e99c9;
	background-color: White;
	margin-bottom: 10px;
	display: block;
}
#Body h1 {
    	float: inherit;
	background-color: #6e99c9;
	text-align: center;
	color: White;
	font-size: 16px;
	font-weight: bold;
	padding: 5px 10px 5px 10px;
	display: block;
    border-bottom: none;
    margin-top: 0px!important;
}
.tablehead {
    background-color: #CCCCCC!important;
    color: Black!important;
}
#Body table {
    border: none;
    padding: 10px;
}
}

@-moz-document url-prefix("https://goodblox.xyz/my/settings/") {
#EditProfileContainer h2 {
	float: inherit;
	background-color: #6e99c9;
	text-align: center;
	color: White;
	font-size: 16px;
	font-weight: bold;
	padding: 5px 10px 5px 10px;
	display: block;
    border-bottom: none;
}
#EditProfileContainer {
	border: 2px solid #6e99c9;
	background-color: White;
	margin-bottom: 10px;
	display: block;
}
}
