css:
@-moz-document url-prefix("https://la.mail.ru/forums") {
 /*основа */.body_wrapper {                      
    padding: 10px 20px 10px 20px;
    background: rgb(102, 102, 102) none;
    -moz-border-radius: 5px;
    -webkit-border-radius: 5px;
    border-radius: 5px;
}
/*Оф форум лостарк надпись */.body_wrapper #pagetitle h1 {
    color: #000000;
    font-size: 18px;
    line-height: 20px;
    margin: 0 0 5px;
}
body a {
    color: #37c515;
}
.body_wrapper .posts li .postbody {
    background: #a0a0a0;;
    border-left: 2px solid #654949;
}
.postbit, .postbitlegacy, .eventbit {
    margin-bottom: 10px;
    display: block;
    width: 100%;
    clear: both;
    position: relative;
    float: left;
    color: #ffffff;
    border: 1px solid #e9e9e9;
}
.body_wrapper .posts li .posthead {
    height: 38px;
    display: flex;
    position: relative;
    justify-content: space-between;
    align-items: center;
    margin: 0px;
    padding: 0px;
    border: none;
    background: #150e0ebf;
}
.postbitlegacy .postrow, .eventbit .eventrow, .postbitdeleted .postrow, .postbitignored .postrow {
    padding: 5px 10px 10px;
    font: 13px Verdana,Arial,Tahoma,Calibri,Geneva,sans-serif;
    color: rgb(0, 0, 0);
}
.body_wrapper .posts li .postdetails {
    background: #202121;
    border-top: 2px solid #792b32;
    border-bottom: 2px solid #484141;
}
.texteditor.forum .cke_skin_kama textarea.cke_source, .texteditor.forum textarea {
    color: #000000;
    font: 13px Verdana,Arial,Tahoma,Calibri,Geneva,sans-serif;
    white-space: pre-wrap;
    background: #a0a0a0 none;
}
.body_wrapper .thread_info .blockbody {
    background: #a0a0a0;
    border: none;
    box-shadow: none;
    color: #5d1313;
}
.postbitlegacy .postrow, .eventbit .eventrow, .postbitdeleted .postrow, .postbitignored .postrow {
    padding: 5px 10px 10px;
    font: 14px Verdana,Arial,Tahoma,Calibri,Geneva,sans-serif;
    color: rgb(0, 0, 0);
}
.body_wrapper .posts li .postfoot .textcontrols {
    padding: 13px 0px 11px;
    background: #202121;
    cursor: default;
}
}
