css:
@-moz-document domain(app.slack.com) {
    .c-message {
        filter: saturate(10%);
    }
    .c-message:hover {
        filter: inherit;
    }
    .c-message_kit__message {
        filter: saturate(10%);
    }
    .c-message_kit__message:hover {
        filter: inherit;
    }
    .c-message__reply_bar .c-avatar {
        display: none;
    }
    .c-message_attachment__border {
        margin-left: -12px;
        margin-right: 12px;
    }
    .c-message_attachment__body {
        padding-left: 0;
    }
    .c-mrkdwn__quote {
        padding-left: 0;
    }
    .c-message blockquote.c-mrkdwn__quote:before {
        background: rgba(255, 255, 255, 0.1);
        left: -12px;
    }
    .c-message_kit__message blockquote.c-mrkdwn__quote:before {
        background: rgba(255, 255, 255, 0.1);        
        left: -12px;
    }

    .p-channel_sidebar div[role="listitem"] {
        height: 22px !important;
        font-size: 14px;
    }

    .p-channel_sidebar .p-channel_sidebar__channel, .p-channel_sidebar__link {
        height: 22px !important;
        line-height: 22px !important;
    }
}
