css:
@-moz-document domain('smotret-anime.online') {
/* Background */
.full-background {
    background: url(https://i.imgur.com/Aypx4Ri.png) top left repeat;
    background-color: #000000;
}

/* Logo */
.brand-logo {
    background: url(https://i.imgur.com/1Zwfb4w.png) center 6px no-repeat;
    background-size: 88%;
}

/* Ads */
.m-news-box-fix,
div.m-news-box-fix-1-inner,
div.card-content.hide-on-small-and-down.m-news-box-fix,
div.card-content.m-news-box-fix.m-news-box-fix-1,
div.card-content.m-new-episodes__description,
div.card-content.m-news-box-fix.m-news-box-fix-right-column.ad-placement,
.m-comments.collection .collection-item.avatar p, .m-comments.collection p,
div.card-content.m-left-vk-group {
    display:none;
}


/* Notifications */
div.onesignal-bell-launcher-button {
    display:none;
}

/* Oh My God */ 
.card,
.card-content,
.m-custom-tabs,
input.ccs-input,
.vjs-default-skin .vjs-control-bar {
    background-color: #35363a;
    color: white;
}

div.m-new-episode.collection-item.avatar {
    background-color: #2d2d2f;
}

.white,
div.card-panel,
.orange,
.qq-uploader {
    background-color: #35363a !important;
}

.grey.darken-4 {
    background-color: #35363a !important;
    border-bottom: 1px solid #35363a;
}

.green.darken-4,
.orange.accent-4,
.orange.darken-4,
.m-block-title a.btn,
.vjs-default-skin .vjs-slider {
    background-color: #404146 !important;
}

.btn.green.darken-4:hover {
    background-color: #2b2b2e !important;
}
.yellow-text.text-darken-4 {
    color: #eff5ed !important;
}
div.m-series-score__title {
    color: rgb(178, 178, 178);
}
.grey-text.text-darken-2 {
    color: #ffffff !important;
}
nav ul.right a,
.button-collapse {
    color: rgba(255, 255, 255, 1.0) !important;
}

a,
h1,
h2,
h3,
div.m-translation-view__date-added,
div.m-translation-view__count-views,
div.m-translation-view-small-series-link,
input,
span.title,
.m-custom-tabs__tab a,
.green-text,
.m-comment-author-name,
.orange-text.text-lighten-3,
.collection.with-header .m-comment.collection-item.avatar,
.dropdown-content li > a,
.dropdown-content li > span,
.select-wrapper span.caret {
    color: white !important;
}

p {
    color: white;
}

div.m-small-title {
    color: rgb(178, 178, 178)
}

.m-select-translation-type .btn.active,
.btn.orange.accent-4:hover {
    background-color: #2b2b2e !important;;
}

.vjs-default-skin .vjs-volume-level {
    background-color: #ffffff;
}

.m-select-view a.active,
.m-select-translation-variant .current {
    color: #656462 !important;
}

[type="checkbox"].filled-in:checked + label:after {
    border: 2px solid #747d7a;
    background-color: #6f7474;
}
.vjs-default-skin .vjs-play-progress {
    background-color: rgb(212, 212, 212);
}
nav ul li:hover,
nav ul li.active,
nav {
    background-color: rgba(54, 55, 53, 1.0);
}

.dropdown-content li:hover,
.dropdown-content li.active,
.dropdown-content li.selected {
    background-color: rgba(38, 40, 38, 0.5);
}
nav ul li.active,
nav .dropdown-content li.active,
nav .dropdown-content li:hover {
    background-color: rgba(51, 54, 51, 0.5);
}

.side-nav a:hover {
  	background-color: rgba(38, 38, 38, 1);
}

.dropdown-content,
.side-nav {
    background-color: #323232;
}

.m-new-episodes,
.m-embed-code textarea {
    background: #2d2d2f;
}

.m-custom-tabs__tab_active {
    border-bottom: 2px solid #ffffff;
}
.orange-text.text-darken-4,
.purple-text.text-darken-4 {
    color: #dbdfe6 !important;
}
.ccs-sugges .dropdown-content li,
.m-comment-new .input-field {
    color: rgb(255, 255, 255);
}
.tag {
    background-color: #64615f;
}
.light-blue.darken-4 {
    background-color: rgba(100, 97, 95, 0) !important;
}

.vjs-default-skin:hover .vjs-big-play-button,
.vjs-default-skin .vjs-big-play-button:focus,
.vjs-default-skin .vjs-big-play-button {
    background: #515255;
    border-color: #515255;
}

.collection.with-header .m-comment.collection-item.avatar {
    background-color: #2d2d2f;
}
.collection .collection-item.m-comment {
    border-bottom: 1px solid #3e3f43;
}

.m-comments.collection.with-header .collection-item {
    border-bottom: 1px solid #202123;
}

.m-comments.collection.with-header .collection-item {
    background-color: #35363a;
}

.collection.with-header .collection-header {
    background-color: #35363a;
	border-bottom: 1px solid #35363a;
	box-shadow: 0 1px 6px -2px rgba(0, 0, 0, 0.6);
  	padding: 5px 20px;
}

.m-comments {
    background: #202123;
}
#onesignal-bell-container.onesignal-reset .onesignal-bell-launcher.onesignal-bell-launcher-theme-default .onesignal-bell-launcher-button svg .background {
    fill: #424346;
}

.btn-floating {
    background-color: #585b5e;
}

.btn-floating:hover {
    background-color: #464646;
}

.m-moments-list-card.slick-slider .slick-dots li.slick-active button:before {
    color: #5d5b5d;
}

.m-group-pop-box,
.m-pop-box {
    background: #4a4a4a;
}

.m-moment__play-button.material-icons {
    color: #5d5b5d;
}

table.highlight > tbody > tr:hover {
    background-color: #2e2e2e;
}

.input-field input:not([type]):focus:not([readonly]),
.input-field input[type=text]:focus:not([readonly]),
.input-field input[type=password]:focus:not([readonly]),
.input-field input[type=email]:focus:not([readonly]),
.input-field input[type=url]:focus:not([readonly]),
.input-field input[type=time]:focus:not([readonly]),
.input-field input[type=date]:focus:not([readonly]),
.input-field input[type=datetime]:focus:not([readonly]),
.input-field input[type=datetime-local]:focus:not([readonly]),
.input-field input[type=tel]:focus:not([readonly]),
.input-field input[type=number]:focus:not([readonly]),
.input-field input[type=search]:focus:not([readonly]),
.input-field textarea.materialize-textarea:focus:not([readonly]) {
    border-bottom: 1px solid #5d5e5e;
    box-shadow: 0 1px 0 0 #5b5b5e;
}

input[type=text]:focus:not([readonly]) + label:not(.md-input),
input[type=password]:focus:not([readonly]) + label:not(.md-input),
input[type=email]:focus:not([readonly]) + label:not(.md-input),
input[type=url]:focus:not([readonly]) + label:not(.md-input),
input[type=time]:focus:not([readonly]) + label:not(.md-input),
input[type=date]:focus:not([readonly]) + label:not(.md-input),
input[type=datetime-local]:focus:not([readonly]) + label:not(.md-input),
input[type=tel]:focus:not([readonly]) + label:not(.md-input),
input[type=number]:focus:not([readonly]) + label:not(.md-input),
input[type=search]:focus:not([readonly]) + label:not(.md-input),
textarea.materialize-textarea:focus:not([readonly]) + label:not(.md-input) {
    color: #5b5d5e;
}

.ccs-chip.chip {
    background-color: #7b7b7b;
}

.ccs-chip-field {
    background-color: #565656;
}

.ccs-sugg .btn.green,
.ccs-sugg .btn.red {
    background-color: #828282 !important;
}

.ccs-sugg .btn.green.checked,
.ccs-sugg .btn.red.checked {
    background-color: #4a4a4a !important;
}

.progress {
  	background-color: #4b4d52;
}

.progress .indeterminate {
    background-color: #7b7e87; 
}
  
.m-comment.m-comment-new.collection-item.avatar .circle {
 	margin-top: 0px; 
}

.divider {
    background-color: #414141;
}

.m-embed-code-link {
	border-bottom: 1px dotted #b0b1ab;
}
  
div.m-comment.m-comment-new.collection-item.avatar {
	background-color: rgb(43, 43, 43);
	box-shadow: inset 0 0 6px 0 rgba(0, 0, 0, 0.6);
}

.container .row {
	margin-left: 0rem;
	margin-right: 0rem;
}

nav, 
nav .nav-wrapper i,
nav a.button-collapse, 
nav a.button-collapse i {
	height: 58px;
	line-height: 58px;
}
  
nav .dropdown-content li > a,
nav .dropdown-content li > span {
	font-size: 15px;
	padding: 14px 10px;
}

div.card-content.empty-msg {
  	padding: 15px;
}

.section {
  	padding-top: 1rem;
	padding-bottom: 0rem;
}

.ccs-genre-op .switch label input[type=checkbox]:checked + .level,
.ccs-genre-op .switch label input[type=checkbox]:checked + .lever {
	background-color: #59575c;
}
  
.ccs-genre-op .switch label input[type=checkbox]:checked + .lever:after {
	background-color: #414243;
}
.switch_label_off.selected, .switch_label_on.selected {
	color: rgba(235, 235, 235, 0.87);
}

.m-alert-success {
    border-color: #414348;
}
.spinner-blue {
    border-color: #e0dbe3;
}
.spinner-red {
    border-color: #96969d;
}
.spinner-yellow {
    border-color: #4c4c4d;
}
.spinner-green {
    border-color: #000000;
}
}
