css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("to-do.live.com") {

.o365sx-navbar {
    background-color: #668964;
}

.o365cs-base .o365sx-button {
    color: #FFFFFF;
    background-color: #668964;
}

.o365cs-base .o365sx-appName, .o365cs-base .o365sx-appName:hover, .o365cs-base .o365sx-appName:visited, .o365cs-base .o365sx-appName:focus {
    color: #FFFFFF;
    background-color: #668964;
}

.o365cs-base .o365sx-button:hover, .o365cs-base .o365sx-button:focus {
    color: #FFFFFF;
    background-color: #5b7a59;
}

.button.blue, .pill-45 {
    background-color: #668964;
}

.button.blue:hover, .pill-45:hover {
    background-color: #5b7a59;
}

.o365header .searchToolbar .fontIcon,
.o365header .searchToolbar .searchToolbar-icon::after {
    color: #aaa;
}



.settingsSection-body a, .settingsSection-body button {
    color: #328ad6; /*#465efc;*/
}


.addGroupButtonWrapper .addGroup-icon .icon svg {
    fill: #328ad6;
}


.baseAdd.addList .baseAdd-input,
.baseAdd.addList:active .baseAdd-input,
.baseAdd.addList:hover .baseAdd-input {
    min-width: 0
}

.baseAdd.addList .baseAdd-input::-webkit-input-placeholder,
.baseAdd.addList:active .baseAdd-input::-webkit-input-placeholder,
.baseAdd.addList:hover .baseAdd-input::-webkit-input-placeholder {
    color: #328ad6
}

.baseAdd.addList .baseAdd-input::-moz-placeholder,
.baseAdd.addList:active .baseAdd-input::-moz-placeholder,
.baseAdd.addList:hover .baseAdd-input::-moz-placeholder {
    color: #328ad6
}

.baseAdd.addList .baseAdd-input:-ms-input-placeholder,
.baseAdd.addList:active .baseAdd-input:-ms-input-placeholder,
.baseAdd.addList:hover .baseAdd-input:-ms-input-placeholder {
    color: #328ad6
}

.baseAdd.addList .baseAdd-input::-ms-input-placeholder,
.baseAdd.addList:active .baseAdd-input::-ms-input-placeholder,
.baseAdd.addList:hover .baseAdd-input::-ms-input-placeholder {
    color: #328ad6
}

.baseAdd.addList .baseAdd-input::placeholder,
.baseAdd.addList:active .baseAdd-input::placeholder,
.baseAdd.addList:hover .baseAdd-input::placeholder {
    color: #328ad6
}

.baseAdd.addList .baseAdd-input::-webkit-input-placeholder,
.baseAdd.addList:active .baseAdd-input::-webkit-input-placeholder,
.baseAdd.addList:hover .baseAdd-input::-webkit-input-placeholder {
    color: #328ad6
}

.baseAdd.addList .baseAdd-input::-moz-placeholder,
.baseAdd.addList:active .baseAdd-input::-moz-placeholder,
.baseAdd.addList:hover .baseAdd-input::-moz-placeholder {
    color: #328ad6;
    opacity: 1
}

.baseAdd.addList .baseAdd-input:-ms-input-placeholder,
.baseAdd.addList:active .baseAdd-input:-ms-input-placeholder,
.baseAdd.addList:hover .baseAdd-input:-ms-input-placeholder {
    color: #328ad6;
    opacity: 1
}

}
