css:
@-moz-document domain("itproportal.com") {
body, #main, .list-text-links .listingResult .content, .listingResult, .rating .icon-star, .rating:before, .input-bar, #articleTag.box .tag, .burgerbar, .popular-box{
    background-color:#222;
}

body, #main, #affiliateDisclaimer, .news-article>header .strapline, #affiliateDisclaimer a, .article .text-copy h2, .article .text-copy h3, .news-article .text-copy h2, .news-article .text-copy h3, .review-article .text-copy h2, .review-article .text-copy h3, .static-article .text-copy h2, .static-article .text-copy h3, .thumbs-feed-01 .syndicatedItem .video-title, .thumbs-feed-01-a .syndicatedItem .video-title, .organic-thumbs-feed-01-c .video-title, .organic-thumbs-feed-01 .video-title, .organic-thumbs-feed-01 .video-description, .exchange-organic-thumbs-feed-01 .video-title, .exchange-organic-thumbs-feed-01 .video-description, .article-name, .impact header a, .listingResult .synopsis, .burgerbar .other-links a, .burgerbar .other-links a:visited, #affiliate-disclaimer, .affiliate-disclaimer__copy a, .affiliate-disclaimer__copy a:active, .affiliate-disclaimer__copy a:visited, .author-block .author{
	color:#ccc;
}

[data-google-query-id], .slot-leaderboard, .skyscraper-left, #sidebar, /*.slot-dynamic-taboola-slot-1, .slot-dynamic-taboola-slot-2, .slot-dynamic-taboola-slot-0, .slot-dynamic-taboola-slot-3,*/ .skyscraper-right, .mobile-leaderboard-320-50, .slot-lightbox1{
	display:none!important;
}

body.limit-width .news-article, #content{
	max-width:none;
    width:auto;
}


.feature-block.top-featured .feature-block-item-wrapper:nth-of-type(2), .feature-block.top-featured .feature-block-item-wrapper:nth-of-type(3){
	border-bottom-color:#222;
    border-right-color:#222;
}

.masthead-item .icon-search .icon-image{
 	fill:#ccc;   
}

.site-logo img{
	background-color:#53A8CE;
    padding:2px;
}
}
