css:
@-moz-document url(https://tradingeconomics.com) {

body, .panel, .panel-footer, tr:hover {
    background: #222 !important;
}
.pagemenu, .navbar, #sidebar, .panel ul, .panel-default>.panel-heading {
    background-color: #29314f !important; /* #4f3e29 */
}
.table th, .table>tfoot>tr>td, .pagetabs, .form-control,
.list-group-item.active, .list-group-item.active:focus, .list-group-item.active:hover  {
    background-color: #463721 !important; /* #29314f */
    color: #ddd;
}
table, #p {
    background-color: #000 !important;
}
.panel-default {
    border-color: #59452b; /* #24324a */
}
.table th, .nav-tabs {
    border-color: #8c6638 !important; /* #3d4975 */
}
.form-control {
    border-bottom: 1px #4a3d2f solid; /* #2f354a */
}
.calendar-iso, a {
    color: #337ab7; /* #ce9d3a */
}
.page-scroll, .nav > li > a {
    color: #3f92d9;
}
.calendar-date-1, body, .title-indicator, .panel-default>.panel-heading {
    color: #999;
}
.calendar-date-2, .calendar-event, #actual, .form-control, h1, h2, h3, h4, h5 {
    color: #ccc;
}
.calendar-item-positive {
    color: #00c100;
}
.calendar-item-negative {
    color: #d80202;
}
.list-group-item {
    background-color: #111;
    border: 1px solid #6a6a6a;
}
.form-control {
    border: 1px #4a3d2f solid;
}
#PanelAllMenus {
    padding-top: 3px;
}
}
