css:
@-moz-document domain('shipstation.com') {

  /*
   * Orders Tab
   */
  /* Hide "Cancel Order" button */
  .action-menu a.lnk-cancel-order {
    display: none;                   
  }

  .notes-from-buyer.active svg,
  .internal-notes svg {
      fill: #ff4642;
  }

  [title="Finale Picked"],
  [data-original-title="Finale Picked"] {
      width: 10px;
  }

  
  [title="Finale Picked"]:before,
  [data-original-title="Finale Picked"]:before,
  [title="Finale Synced"]:before,
  [data-original-title="Finale Synced"]:before {
      background-color: #fff;
      border-radius: 4px;
      /* content: "\01F431"!important; */ /* Kitty cat emoji */
      position: absolute;
      display: inline-block;
      margin-left: 3px;
      width: 20px;
      margin-right: 3px;
      left: -10px;
      font-size: 20px;
      font-style: normal;
      z-index: 999;
  }

 	[title="Finale Synced"]:before,
 	[data-original-title="Finale Synced"]:before {
     	width: 25px;
    	background: center / contain no-repeat url(https://alternative.me/icons/finale-inventory.png);
    	background-color: white!important;
    	color: white!important;
    	height: 25px;
    	border-radius: 0;
}


  
  
	[title="Finale Picked"]:before,
 	[data-original-title="Finale Picked"]:before {
    	content: "\01F4E6"!important; /* Package emoji */
  }
  
  /*
   * Single Order View
   */

  .internalNotes>td>a:not(.editable-empty),
  .notesFromBuyer a:not(.editable-empty) {
      color: #ff4642;
      font-weight: bold;
  }

  .order-detail [title="Finale Picked"],
  .order-detail [data-original-title="Finale Picked"] {
      right: -8px;
  }

}
