css:
@-moz-document url-prefix("https://wis.fit.vutbr.cz/") {
/* default font */
body, td, iframe, .text {
    color: white;
}

/* links */
a:link, a:visited {
    color: #96cafe;
}

/* home buttons */
a.sbtn {
    background: #2f3136;
    box-shadow: 0 0px 0px;
    color: white !important;
}
a.sbtng {
    background: #202225;
    box-shadow: 0 0px 0px;
    color: white !important;
}
a.sbtno {
    background: #202225;
    box-shadow: 0 0px 0px;
    color: white !important;
}

/* curent tab marker */
a.stabs {
    color: white;
    background-color: #36393f;
}
a.stab{
    color: white;
}

/* tab separators */
th {
    border-left: 1px solid #36393f !important;
}

/* background color */
body {
    background: #36393f;
}
}

@-moz-document url-prefix("https://wis.fit.vutbr.cz/FIT/st/study") {
hr {
    border-top: 1px solid #36393f;
}

/* links */
a:link, a:visited {
    color: #96cafe;
}
table table a:link, table table a:visited {
    color: #003365;
}

/* table text adjustment */
tbody tbody>tr>:nth-child(n){ 
    color: black;
}

/* tabs */
a.stab, a.stabs{
    color: #f6f6f7;
}

table tbody {
    outline-style: none;
    border-bottom: 2px solid #36393f;
    border-left: 2px solid #36393f;
    border-right: 2px solid #36393f;
}
}

@-moz-document url-prefix("https://wis.fit.vutbr.cz/FIT/st/news") {
/* links */
a:link, a:visited {
    color: #96cafe;
}
table table a:link, table table a:visited {
    color: #003365;
}

/* tabs */
a.stab, a.stabs{
    color: #f6f6f7;
}

/* table text adjustment of columns 2 through 4*/
tbody>tr>:nth-child(n+3):nth-child(-n+4){ 
    color: black;
}
}

@-moz-document url-prefix("https://wis.fit.vutbr.cz/FIT/st/course") {
hr {
    border-top: 1px solid #36393f;
}

/* link */
table table a:link, table table a:visited {
    color: #003365;
}
}

@-moz-document url-prefix("https://wis.fit.vutbr.cz/FIT/st/course-h2"), url-prefix("https://wis.fit.vutbr.cz/FIT/st/course-reg") {
/* table text adjustment */
tbody tbody>tr>:nth-child(n){ 
    color: black;
}
}

@-moz-document url-prefix("https://wis.fit.vutbr.cz/FIT/st/studygs") {
/* text */
tbody>tr>:nth-child(n+1):nth-child(-n+5) {
    color: white;
}

/* links */
table table a:link, table table a:visited {
    color: #96cafe;
}
}

@-moz-document url-prefix("https://wis.fit.vutbr.cz/FIT/st/cfs"), url-prefix("https://wis.fit.vutbr.cz/FIT/st/osinfo.php"), url-prefix("https://wis.fit.vutbr.cz/FIT/st/email"), url-prefix("https://wis.fit.vutbr.cz/FIT/st/bci") {
tbody tbody a:link, tbody tbody a:visited{
    color: #2994ff;
}

/* table text adjustment */
tbody tbody>tr>:nth-child(n){ 
    color: black;
}

/* tabs */
a.stab, a.stabs{
    color: white !important;
}
}

@-moz-document url("https://wis.fit.vutbr.cz/FIT/") {
/* LOGIN PAGE */
/* LOGO VUT FIT on login page */
table tbody img {
    width: 270px !important; /* new width */
    height: 0px;
    padding: 38px;  /* new height */
    background: url(https://www.vutbr.cz/data_storage/multimedia/jvs/loga/02_fakulty/FIT/1-zakladni/CZ/PNG/FIT_barevne_RGB_CZ.png);
    background-size: cover;
}
/* fix of top left logo on login page because of that ^^^ */
table.noprint img{
    width: 50px !important;
    height: 0px !important;
    padding: 19px;
    background: url(https://wis.fit.vutbr.cz/images/fitnewz.png);
    background-size: cover;
}
}
