@-moz-document domain("ttime.cumbria.ac.uk") {
  
/************************************************************/
/******** University of Cumbria Timetable Dark Theme ********/
/************************************************************/


body {
	background-color: #121212 !important;
    color: #FFFFFF !important;
    font-family: 'Roboto Condensed', sans-serif !important;
}


/*********************************/
/********** CHOICE PAGE **********/
/*********************************/


/* Title */
#Title {
	top: 20px;
	color: #03a9f4;
	font-weight: bold;
	font-size: 26px;
}

/* Left Links */
#pLinkPanel {
	border-style: none;
	background: #121212;
}

/* Link Text */
.LinkButton, .LinkButtonSelected, a:link, a:hover {
	color: #03a9f4;
	text-decoration: none;
}

/* Wildcard Text */
#lWildcard, h3, h4 {
    color: #03a9f4;
}

/* Dropdowns */
.DepartmentFilter {
    background-color: #212121;
    border: none;
    color: #fff;
}

/* Search Bar */
.modfiltercontrol {
  border: none;
  background-color: #212121;
  color: #fff;
  padding-top: 3px;
  padding-bottom: 3px;
}

 /* Buttons */
.modfilterbutton, .ViewTimetableButton {
  background-color: #212121;
  border: none;
  color: #fff;
  padding-top: 3px;
  padding-bottom: 3px;
}

/* Buttons Hover */
.modfilterbutton:hover, .ViewTimetableButton:hover {
  background-color: #424242;
  border: none;
  color: #fff;
  padding-top: 3px;
  padding-bottom: 3px;
}

/* Error Table */
.ErrorTable {
	border-color: #03a9f4;
}

/* Footer White Bar */
hr {
    border-style: solid;
	color: #fff;
    margin-top: 40px;
}

/* Random unclassed, un id-ed red text */
#tInfoBox > p:nth-child(3) > b:nth-child(1) > font:nth-child(1), h5 {
    color: #fff;
}




/*********************************/
/*********** MAIN PAGE ***********/
/*********************************/


/******** ABOVE TIMETABLE ********/


/* Timetables 2019/20 */
html body table.header-border-args tbody tr td table.header-0-args tbody tr td span.header-0-0-0 table tbody tr td font {
    font: 20pt 'Roboto Condensed', sans-serif !important;
}

/* Programme: BSc (Hons) - Outdoor Adventure and Environment (Yr2 Ambleside) and week info */
html body table.header-border-args tbody tr td table.header-3-args tbody tr td span.header-3-0-0 table tbody tr td font {
    font: 12pt 'Roboto Condensed', sans-serif !important;
}




/******** TIMETABLE ********/

/* Space between timetable and header */
.grid-border-args {
    padding-top: 20px;
}

/* Table Times */
td.col-label-one {
    background-color: #e53935;
    border-top: 1px solid #000;
	border-left: 1px solid #000;
	font: 10pt 'Roboto Condensed', sans-serif !important;
    padding-top: 5px;
    padding-right: 10px;
    padding-bottom: 5px;
    padding-left: 10px;
}

/* Table Days */
td.row-label-one {
    background-color: #e53935;
	border-top: none;
    border-left: 1pt solid #000;
    font: 10pt 'Roboto Condensed', sans-serif !important;
    padding-top: 10px;
    padding-bottom: 10px;
}

/* Timetable */ 
table.object-cell-args {
    background-color: #212121;
	color: #FFFFFF;
    font: 10pt 'Roboto Condensed', sans-serif !important;
}

/* Lecture Cells */
td.object-cell-border {
    background-color: #212121;
    border: none;
    border-right: 1px solid #000;
    border-bottom: 1px solid #000;
    padding-top: 10px;
    padding-right: 15px;
    padding-bottom: 10px;
    padding-left: 15px;
}

/* Other Cell Borders (It's all very confusing) */
td.cell-border {
	border-top: none;
    border-left: none;
}




/******** BELOW TIMETABLE ********/

/* Timetables are subject to change - please check regularly */
html body table.footer-border-args tbody tr td table.footer-2-args tbody tr td span.footer-2-0-0 table tbody tr td font span.footer-2-0-4 {
    color: #FFFFFF !important;
    font: 15pt 'Roboto Condensed', sans-serif !important;
}

/* Printer Friendly */
html body table.footer-border-args tbody tr td table tbody tr td font a#bPrint {
    color: #FFFFFF !important;
    font: 10pt 'Roboto Condensed', sans-serif !important;
    text-decoration: none;
}

/* Previous Week */
html body table.footer-border-args tbody tr td table tbody tr td font a#bLastWeek {
    color: #FFFFFF !important;
    font: 10pt 'Roboto Condensed', sans-serif !important;
    text-decoration: none;
}

/* Next Week */
html body table.footer-border-args tbody tr td table tbody tr td font a#bNextWeek {
    color: #FFFFFF !important;
    font: 10pt 'Roboto Condensed', sans-serif !important;
    text-decoration: none;
}

/* Back To Selection */
html body table.footer-border-args tbody tr td table tbody tr td font a#bBackSelection strong font {
    color: #FFFFFF !important;
    font: 12pt 'Roboto Condensed', sans-serif !important;
}

/* Back To Selection remove underline */
html body table.footer-border-args tbody tr td table tbody tr td font a#bBackSelection {
    text-decoration: none;
}

/* Time and Date */
html body table.footer-border-args tbody tr td table tbody tr td font {
    font: 8pt 'Roboto Condensed', sans-serif !important;
}
}
