css:
/*
     * Name:    DarkTheme For CloudPortal SolveTT
     * Author:  WesleyK
     * Version: v1.0.0
     */
@-moz-document domain(solveit.nl) {
html, body {
    background: #828282;
}
#left {
    position: absolute;
    top: 0px;
    left: 0px;
    transition: width 0.28s cubic-bezier(0.4, 0, 0.2, 1);
    height: 100%;
    background: #353535;
}
#left #groupsSelect + div {
    background: #101010;
}
#left div.aboutBtn #logo_canvas {
    width: 29px;
    height: 29px;
    margin-left: 2px;
    margin-top: 2px;
    float: left;
    background: rgba(80, 80, 80, 0.7);
    border-radius: 50%;
    position: relative;
}
#left div.items span div.icon {
    background: #101010;
}
#left div.items div.arrow {
    background: #101010;
}
#left div.items {
    margin-top: 5px;
    padding-top: 10px;
    height: calc(100% - 44px);
    background: #2d3031;
    border-right: 1px solid #000000;
    transition: margin-top 0.28s cubic-bezier(0.4, 0, 0.2, 1);
}
#left div.items div.sep {
    float: right;
    width: 100%;
    height: 3px;
    background: #676767;
    margin-bottom: 10px;
}
#left div.items span {
    border-radius: 25px 35px 20px 25px;
    float: right;
    width: 141px;
    margin-right: 2px;
    margin-bottom: 10px;
    display: block;
    text-align: center;
    /*border: 1px solid #0D47A1;*/
    cursor: pointer;
    box-shadow: 0 0px 2px 0 rgba(0, 0, 0, 0.14), 0 1px 5px 0 rgba(0, 0, 0, 0.12), 0 0px 1px -2px rgba(0, 0, 0, 0.2);
    transition: box-shadow 0.28s cubic-bezier(0.4, 0, 0.2, 1);
    background: #5a5a5a;
    color: white;
    line-height: 24px;
    height: 24px;
    -webkit-user-select: none;
    /* Chrome/Safari */
    -moz-user-select: none;
    /* Firefox */
    -ms-user-select: none;
    /* IE10+ */
}
#tabbar {
    background: #383838;
    box-shadow: 0 2px 2px 0 rgba(0, 0, 0, 0.14), 0 1px 5px 0 rgba(0, 0, 0, 0.12), 0 3px 1px -2px rgba(0, 0, 0, 0.2);
    position: fixed;
    left: 147px;
    width: calc(100% - 147px);
    height: 34px;
    transition: top 0.28s cubic-bezier(0.4, 0, 0.2, 1), left 0.28s cubic-bezier(0.4, 0, 0.2, 1), width 0.28s cubic-bezier(0.4, 0, 0.2, 1);
    z-index: 1;
}
#tabbar #chatBtn div.img {
    background: #828282;
}
#tabbar #groupbeheer div.img {
    background: #828282;
}
input[type="button"] {
    background: #f1f1f1;
}





/* Secured */
table.fancy thead tr th, table.fancy thead tr td {
    color: #ffffff;
    background-color: #383838;
    height: 25px;
    text-align: left;
    font-size: 15px;
    padding: 0px;
}
table.fancy tbody tr td {
    border-bottom: 1px solid #000000;
    background-color: #424242;
    color: #d0d0d0;
}
table.fancyh tbody tr:hover td {
    cursor: pointer;
    background-color: #5d5d5d!important;
}
div.tabbar_r {
    background: #484848;
}
div.legend {
    background: #383838;
}
div.fieldset {
    background: #797979;
}
::-webkit-scrollbar-thumb {
    background: #e6e6e6;
}
div.label-and-input-container input {
    color: #ffffff;
    border-bottom: 1px solid #fdfdfd;
}
div.label-and-input-container input.fullNonActive + div.line + label {
    color: #d4d4d4;
}
input[type="button"] {
    background: #171717;
}
div.label-and-input-container label {
    color: #ffffff;
}






/* Adresboek */
div.innerTabBar {
    background: #383838;
}
}
