/*
 Hacker News Better Mobile
*/
@media screen and (max-width: 600px) {

  /* 
    	The vote arrows are so small that I can never know what 
      I'm tapping on so I increased the size
    */

  .votearrow {
	
    min-width: 30px;

    min-height: 30px;
    
    background-image: none;
    position: relative;

    background-position: center;

    border-radius: 8px;

    background-color: rgba(255, 255, 255, 0.5);

    border: 1px solid rgba(0, 0, 0, 0.2);

  }

  .votearrow:active {

    background-color: rgba(0, 0, 0, 0.2);

  }
  
  .votearrow:after {
    position: absolute;
    top: 10px; left: 9px;
	  display: block;
    content: '';
    width: 0; 
	  height: 0; 
  	border-left: 6px solid transparent;
  	border-right: 6px solid transparent;
  	border-bottom: 9px solid #9a9a9a;
}
  
  /* same deal with the toggle button */
  .togg {
  	display: inline-block;
		text-align: center;
    padding: 10px;
    border-radius: 8px;
    background-color: rgba(255, 255, 255, 0.5);
    border: 1px solid rgba(0, 0, 0, 0.2);
  }
 

}
