css:
@-moz-document url-prefix("https://www.rbxleaks.com"){

:root{/*[[colorscheme]]*/}

/*[[logo]]*/

body {
    background-color: var(--t)!important;
}
/*NAVBAR*/
.side-nav {
    background-color: var(--t1);
    width: 240px;
}
.side-footer {
    color: var(--t3)!important;
    border-top: 1px solid var(--t2)!important;
    position: absolute;
}
.logo {
    color: var(--white);
    background-color: var(--t1)!important;
    font-size: 32px;
    border-bottom: 1px solid var(--t2)!important
}
/*CATALOGUE*/
.card {
    background-color: var(--t4)!important;
    border-radius: 5px!important;
    border: 0px;
}
.sub-title {
    color: var(--t5);
    font-size: 24px;
    margin: 50px 0 10px 0.75rem;
    font-weight: 500
}
.card-title {
    color: var(--white)!important
}
.toggle {
    background: var(--t6);
    color: var(--t7)
}
.toggle:hover {
    background: var(--t8);
    color: var(--t7)
}
.card .card-reveal {
    -webkit-filter: invert(100%);
    filter: invert(100%);
    color:var(--black)
}
.card .card-action {
    border-top: 1px solid var(--t2);
}
.card-action .col s10 {
    color: var(--white)!important
}
.tabs {
    background: var(--t3);
    border: 0px
}
.card .card-action a:not(.btn):not(.btn-large):not(.btn-large):not(.btn-floating) {
    color: var(--white)
}
/*COMMENTS*/
.collection .collection-item {
    background-color: var(--t9);
    line-height: 1.5rem;
    padding: 10px 20px;
    margin: 0;
    border-bottom: 1px solid var(--t2);
}
.collection {
    border: 0px!important
}
.collection .collection-item {
    color: var(--t7)
}
/*FORUM*/
.title {
    color: var(--t5);
    font-size: 24px;
    margin: 50px 0 10px 0.75rem;
    font-weight: 500
}
.forum.title-bar {
    background-color: var(--t9);
    color: var(--white);
    padding: 10px;
    overflow: auto;
    width: 100%;
}
.forum.thread-link-card {
    background-color: var(--t10);
    max-height: 500px;
    border: 0px!important;
    padding: 10px !important;
    margin-top: -1px !important;
    color: var(--white)
}
.forum.thread-link-card:hover {
    background-color: var(--t11);
}
.forum.line a {
    color: var(--t5)!important;
}
.forum.line {
    color: var(--white)!important;
}
.forum.thread-text {
    word-wrap: break-word;
    white-space: pre-wrap;
}
.forum.user-name {
    color: var(--white) !important
}
.forum.admin-banner {
    background-color: var(--black);
}
.forum.pinned-banner {
background-color: var(--black);
}
.forum.thread-link {
    color: var(--t5);
}
.forum.thread-card {
    background-color: var(--t11)!important;
    max-height: 500px;
    border: solid var(--t11) !important;
    border-width: 15px;
    box-shadow: inset 4px 0 0 0 var(--white);
    color: var(--white);
    padding: 15px !important;
    margin-top: 0px !important;
    margin-bottom: 10px !important;
}
.forum.reply-card {
    background-color: var(--t11);
    max-height: 400px;
    border: 0px!important;
    color: var(--white);
    padding: 10px !important;
    margin-top: 5px !important;
    overflow: auto;
}
.forum-pagination li.active {
    background-color: var(--t11);
}
.forum.button {
    background-color: var(--t11)!important
}
.forum.reply-box {
    background: var(--t9)!important;
    border: 0px!important
}
/*SETTINGS*/
.main .container .box {
    background: var(--t11);
    border: 0px
}
input:not([type]),
input[type="text"]:not(.browser-default),
input[type="password"],
input[type="email"],
input[type="url"],
input[type="time"],
input[type="date"],
input[type="datetime"],
input[type="datetime-local"],
input[type="tel"],
input[type="number"],
input[type="search"],
textarea.materialize-textarea {
    border-bottom: 1px solid var(--t2);
}
/*PROFILE*/
.container .row {color: var(--white)}
.favorite-leak-title {color: var(--white)}

.adsbygoogle{display: none}
.banner{background-color:var(--black)}
}

style_setting_options.value:
.logo {
		color: #fff;
		font-size: 92px;
		font-family: "ubuntu";
		font-weight: 700;
		font-family: "Josefin Sans", sans-serif;
		background: repeating-linear-gradient(to left, #3d2ae7, #b05bff, #3d2ae7);
		background-size: auto auto;
		background-size: 200% auto;
		background-clip: text;
		text-fill-color: transparent;
		-webkit-background-clip: text;
		-webkit-text-fill-color: transparent;
		animation: textclip 1.5s linear infinite;
		display: inline-block;
	}

@keyframes textclip {
	to {
		background-position: 200% center;
	}
}
