css:
@-moz-document domain("classlink.com") {
/*Background Color*/

body {
    background-color: #303030
}

/*Loading Screen*/

.login .btn-primary {
     display: inline-block;
    font-size: 15px;
    line-height: 120%;
    color: #0f0 !important;
    background-color: transparent;
    border: 2px solid #0f0 !important;
    padding:10px 12px;
    border-radius: 4px;
    text-decoration: none;
    cursor: pointer;
    transition: background-color ease 400ms;
    -webkit-box-shadow: 0px 4px 0px 0px rgba(0,255,0,0.3);
}

.login .btn-primary:hover {
    color: #fff !important;
    background-color: rgba(0,255,0,0.3) !important;
}

.btn-primary.saml {
    display: inline-block;
    font-size: 15px;
    line-height: 120%;
    color: #e77f33 !important;
    background-color: transparent !important;
    border: 2px solid #e77f33 !important;
    padding:10px 12px;
    border-radius: 4px;
    text-decoration: none;
    cursor: pointer;
    transition: background-color ease 400ms;
    -webkit-box-shadow: 0px 4px 0px 0px #e77f334d !important;
}

.btn-primary.saml:hover {
    color: #fff !important;
    background-color: #e77f334d !important;
}

.btn-primary.google {
    display: inline-block;
    font-size: 15px;
    line-height: 120%;
    color: #4285f4 !important;
    background-color: transparent !important;
    border: 2px solid #4285f4 !important;
    padding:10px 12px;
    border-radius: 4px;
    text-decoration: none;
    cursor: pointer;
    transition: background-color ease 400ms;
    -webkit-box-shadow: 0px 4px 0px 0px #4285f44d !important;
}

.btn-primary.google:hover {
    color: #fff !important;
    background-color: #4285f44d !important;
}

.btn-primary.qrcode {
    display: inline-block;
    font-size: 15px;
    line-height: 120%;
    color: #26abcd !important;
    background-color: transparent !important;
    border: 2px solid #26abcd !important;
    padding:10px 12px;
    border-radius: 4px;
    text-decoration: none;
    cursor: pointer;
    transition: background-color ease 400ms;
    -webkit-box-shadow: 0px 4px 0px 0px #26abcd4d !important;
}

.btn-primary.qrcode:hover {
    color: #fff !important;
    background-color: #26abcd4d !important;
}

#welcome_ploadingpanel {
    background: #272727
}

/*Logo in loading Screen*/

#welcome_ploadingpanel .logo {
    background: #404040
}

#welcome_ploadingpanel .logo img {
    background: url(https://cdn2.scratch.mit.edu/get_image/user/34194727_60x60.png)
}

#ploadingpanel .splash-message, #welcome_ploadingpanel .splash-message {
    color:#0f0
}

/*Navbar*/

.navbar-inverse .navbar-inner {
    background-color: #212121;
    border-bottom: solid 1px #0f0 !important;
}

/*Buttons in navbar*/

.clnav .nav > li > a > i {
    color: #0f0
}

.clnav .brand:hover, .clnav .nav > li > a:hover i,.cl-collapse-right > ul > li:hover i{
    border-color: #0f0
}

.user_popup {
    background: #404040;
    box-shadow: 0 2px 10px #0f0;
    color: #0f0
}

.user_popup h2 {
    color: #0f0
}
}
