css:
@-moz-document domain(soundcloud.com) {
  .badgeList__item .image
  {
    opacity: 0.05;
  }

  .listenArtworkWrapper .image, .searchItem div.image
  {
    opacity: 0.02 !important;
  }

  .playButton.m-stretch, .show .header__logo
  {
    background: black;
  }

  .fullHero__title .soundTitle__usernameHeroContainer
  {
    display: none;
  }

  .sidebarModule.mobileApps, .l-footer.standard, .autoTagger.loadedWithData, .trackItem__image
  {
    display: none;
  }

  .fullHero__playerArea
  {
    opacity: 0.3;
  }

  .sc-artwork{
    opacity: 0.1;                  
   }
  .l-listen__mainContent>.l-about-left, .listenDetails .listenAttributes, .paging-eof, .fullHero__info, .header__userNav, .show .header__logo
  {
    display: none;
  }

  .soundTitle.listenContext .soundTitle__title
  {
    font-size: 13px;
  }

  .soundBadge__artwork.sc-media-image span
  {
    background-image: none;
  }
}
