css:
@-moz-document domain("zeit.de") {
* {
    font-family: Serif;
}
#iqd_mainAd,
.ad-container, 
.nextread-advertisement 
{
    display:none !important;
}

.page__content,
body, 
header[data-ct-area="topnav"].header, 
header[data-ct-area="topnav"] .nav,
.zon-dossier__kicker,
.buzzboard__headline,
.podcast-bar__headline,
.podcast-bar__more,
.zon-teaser-printbox__heading,
.footer-publisher, .footer-publisher div,
.footer-links,
.footer-links__row,
.search__button,
.search-form,
.cp-area--ranking,
.topicbox__supertitle,
.comment-preferences,
.markup--standard, 
.article-teaser {
    box-shadow: none ;
    background-color: #f5f5f5 !important;
}
textarea {
    border: 1px solid #ccc;
    border-radius: 5px;
}
input, textarea, select,.search__input {
    background-color: #f9f9f9 !important;
}
.search__button:hover {
    border-color: #aaa !important;
    background-color: #eee !important;
}
input:hover, textarea:hover, select:hover,.search__input:hover {
    border-color: #aaa !important;
}
input:focus, textarea:focus, select:focus,.search__input:focus {
    background-color: white !important;
}
.abo-nl-box .form__svg-container {
    background: none;
}


.footer-links__row a {
    color: #444;
}
.footer-links__row a:hover {
    color: #111;
}

.header__logo,
.zon-teaser-printbox__media-item,
#overscrolling_target {
    filter: brightness(96%);
}
.zon-teaser-wide__media-container:hover,
.zon-teaser-standard__media-container:hover,
.zon-teaser-classic__media-item:hover,
.zon-teaser-podcast__media-item:hover,
.header-article__media-item:hover,
.figure__media:hover {
    transform: scale(1.02);
    filter: /*drop-shadow(0px 10px 10px rgba(0,0,0,.4)) */ contrast(110%) brightness(110%);
}
.zon-teaser-wide__media-container,
.zon-teaser-standard__media-container,
.zon-teaser-classic__media-item,
.zon-teaser-podcast__media-item,
.header-article__media-item,
.figure__media
{
    transition: all 0.2s ease;
}

.header__brand:hover {
    transform: scale(1.1);
}
.header__brand {
    transition: all 0.2s ease;
}

.article__media,
.comment-overlay,
.markup__text--wrapped:after,
.article-flexible-toc__showall:after {
    background-image: linear-gradient(0deg, #f5f5f5, rgba(245,245,245,0))
}
.js.no-touchevents .podcast-bar__list-arrow--right {
    background-image: linear-gradient(90deg, rgba(245,245,245,0) 0%, #f5f5f5 60%, #f5f5f5 100%);
}
.js.no-touchevents .podcast-bar__list-arrow--left {
    background-image: linear-gradient(90deg, #f5f5f5 0%, #f5f5f5 40%, rgba(245,245,245,0) 100%)
}

.comment--indented .comment__container:after {
    border-bottom-color: #f5f5f5;
}
*[data-label="Anzeige"] {
    display: none !important; 
}
}
