.international-header .mini-header::before {
    content: '';
    position: fixed;
    top: 0;
    left: 0;
    width: 100vw;
    height: 100vh;
    background-color: rgba(0, 0, 0, 0);
    transition: .25s ease-in-out;
    visibility: hidden;
	z-index: -1
}

.international-header .mini-header:focus-within::before {
    background-color: rgba(0, 0, 0, .5);
    visibility: visible;
}

 .international-header:focus-within::after {
    backdrop-filter: var(--filter-glass);
}

.international-header .nav-search::after{
    content:'';
    position:absolute;
    top: -60px;
    right: -10px;
    width: 100px;
    height: 100px;
    background:url(https://i.loli.net/2020/06/23/E3he95P4XrLkGMm.png);
    background-size: cover;
    z-index: -1;
    transition: .25s ease-out;
    opacity: 0;
}

.international-header .nav-search:focus-within::after{
    transform:translatey(-20px);
    opacity: 1;
}
