css:
@-moz-document url-prefix("https://www.freecodecamp.org") {
body{
    font-family: Open Sans, sans-serif;
}

h1, h2, h3, h4 {
    font-family: Open Sans, sans-serif;
}

code {
    font-size: 1em;
    font-family: Consolas, monospace;
}

button{
    outline: none;
}
.btn{
    border-color: var(--quaternary-background);
}
.horizontal.reflex-element{
    padding-top:18px;
    background: white;
}
:root{
    --header-height: 0px;
}
header::before {
    position: fixed;
    left: 6px;
    content: "â˜°";
    font-size: 1.5em;
    font-weight: bold;
    color: darkgrey;
}
.universal-nav{
    height: 42px;
    width: 700px;
    opacity:0;
    border-bottom-right-radius: 18px;
}
#universal-nav:hover{
    transition: 250ms;
    opacity:1;
}
.universal-nav-middle{
    flex: 0 0 0;
}
.universal-nav-left{
    flex: 2;

}
.map-title-completed{
    font-size: 0.8em;
}
}
