css:
/* версия 1.8 от 04.08.2022 */
@-moz-document domain("livelib.ru") 
{

h1, h2.h1 {
    margin-bottom: 18px;}
.p, p {
    font-size: 16px;
    line-height: 20px;}

/* ФОРМЫ (поиска в 1 очередь) */
.div-form-search {
    padding: 16px;}
#userbook-form-div .div-form-search {
    background: #d1ecff;
}
.search-right-more + h1 + .div-form-search {
    background: #d1ecff;}

/*--------------------------------------------*/
/* Убрать надпись МОИ КНИГИ из своего профиля */
/*--------------------------------------------*/
.swiper-wrapper[data-object_id="Plushkin"] .scifi-selected-cover .cover-wrapper .bg-book-text.ub{
    display: none;}
/*--------------------------------------------*/

.card-block .group-title {
    padding: 7px 24px;
}

/* убираем кое-что из рекламы */
.subcontainer.on-left > div > div[id^="smialek"], div[class*="mixedly"] {
    display:none;
}
.subcontainer.on-right > div > .column-fixed div[id="side-ajax"] {
    display:none !important;
}

/* самое верхнее меню */
.header .outer, .header-outerv4, .header .menu-main, .header .outer .middle ul.nav li a {
	height: 48px;}

.header .menu-main, .header .outer .middle ul.nav li a {
    line-height: 48px;}

/* шапка-профиль с картинкой */
#profile-bg-wrapper.isset-profpic {
    margin-top: 0px !important;}
.gp-wrapper .gp-table {
    height: 120px;
}

	.user-profile[style] {background-color:#d1ecff !important;}
    div.profile-header-details, div.user-profile {height:auto; }
	div.header-container {height:auto !important;}
	.profile-header-details {height: 120px;}
	div.profile-avatar {width: 84px; height: 84px;}
	table.profile-header-details br {display: none;}
	.header-profile-status {margin-left: 10px; font-size: 12px; margin-bottom: 6px;}
	table.profile-header-details tr td {margin: 0; padding: 15px 0 5px 20px; vertical-align: top;}
	table.profile-header-details tr td.profile-header-avatar {padding: 1px 20px;}

/* меню под профилем */
  .header-context.profile-context {height: 40px; line-height: 40px; background-color: #f8f8f8;}
  .header .header-context.profile-context ul.nav.context li.active {height: 36px; border-bottom-width: 4px;}
  .header .header-context ul.nav.context li {padding: 0 0px}
  .header .header-context.profile-context ul.nav.context li {height: 40px; line-height: 40px;}
  .header .header-context.profile-context ul.nav.context li a {font-size: 14px; padding: 0 4px;}
  .header .header-context.profile-context #more-li {height: 40px; background-color: white;}
  .header .header-context.profile-context #more-li {line-height: 40px;}
  div.header-container div.usermenu {top: 20px !important;}
  
  .header .header-context {height: 40px; line-height: 40px;}
  .header .header-context ul.nav.context li {display: inline-block; height: 40px; line-height: 40px;}
  .header .header-context ul.nav.context li.active {height: 36px;}
	.header .header-context .menu-group ul.nav.context li#more-li {
    height: 40px;} /* это прямо в профиле */

/* информокошки, комментарии */
.card-block.with-pad, .card-block .with-pad {
    padding: 12px 14px;
}
.rback .group-event-actionbar, .card-block .rback .backgr-block .group-event-actionbar {
    padding-left: 14px;
    padding-right: 14px;
}
.group-event-actionbar {
    padding: 12px 14px;
}
.card-block {
    margin-bottom: 18px;
}
.pager-top {
    padding-top: 12px}

.comments .card-block .comment-inner-view {
    padding: 12px 10px;
    margin-top: -1px;
}
.comment-top .event-actionbar {
    padding: 12px 0px 0px;
}

/* блок ИНФОРМАЦИЯ в профиле */
.group-row-title {
    margin-bottom: 8px;
    line-height: 18px;
}
.profile-info-column .group-row-title:nth-child(3) > b+a, .profile-info-column .group-row-title:nth-child(4) > b+a, .profile-info-column .group-row-title:nth-child(5) > b+a, .profile-info-column .group-row-title:nth-child(6) > b+a {
    display: block;
    /*width: 50%;*/
    overflow: hidden;
    text-overflow: ellipsis
}
.with-pad>div[style] {
	margin-bottom: 10px !important;}
.label-orange, .label-genre, .label-tag, .label-spoiler, .label-red {
    margin: 0 10px 10px 0;}
.expert-sign {
    margin: 0px 0px 10px 0px;}
/* ПРОЧИТАЛ, ХОЧУ */
.blist-compact .brow.brow-compact .brow-inner {
    padding: 16px;
}
.blist-biglist .brow-cover .cover-wrapper img{
    width: 120px;
    min-width: 120px !important;
    min-height: 170px !important;
}

/* блок ДРУЗЬЯ и иже с ними */
.ulist-smalltiles .urow .urow-login {
    font-size: 14px;
    width: 76px;
}
.ulist-smalltiles .urow {
    width: 76px;
    min-height: 76px;
    margin: 20px 0 0 20px;}

.ulist-smalltiles .urow .urow-avatar {
    border-radius: 3px;
    width: 76px;
    height: 76px;
}
.popup-likes-context {
    padding: 0 10px;
}

/* ЦИТАТЫ в профиле */
.group-quote-block .quote-book-wrapper {
    width: 148px;
    padding: 16px 0 16px 16px !important;
}
.group-quote-block .quote-back-left {
    width: 148px;
}
.group-review .group-quote-block td[style] {
   width: 148px !important;
}
.carousel-scifi-books div[data-object_id*="Plushkin"] .swiper-slide .book-data {
    display: none;
}
.group-quote-block .quote-wrapper .quote-container.quote-middle {
    background-size: 63px 50px !important;
    padding: 40px 40px 20px 20px;
}
.group-quote-block .quote-wrapper .quote-container .quote-book-authorpic {
    width: 46px;
    height: 46px;
}
.quote-book-wrapper .book-cover {
    width: 120px;
}

.carousel-scifi-books .cover-wrapper img {
    width: 120px;
    min-width: 120px !important;
    min-height: 170px !important;
   /* margin-left: 10px;*/
}
.card-block .backgr-block {
    border-radius: 16px;
}
#favorites-list .card-block .group-review .card-block {
    border-radius: 0px;
    border: 0px solid #D2D4D6;
}
#favorites-list .card-block .group-review .card-block .quote-back-left {
    border-radius: 0;
}
.group-review.review-inner .group-quote-block.card-block.backgr-block {
    border-radius: 16px;}



/* КАРУСЕЛИ с КНИГАМИ */
.swiper-carousel.carousel-scifi-books .swiper-slide {
    width: 138px;
    margin: 10px 8px 10px 10px;
}
.swiper-slide .book-data .userbook-container {
    width: 120px;
}

/* подгонка контейнера в старом виде рецек */
.review .card-block-text .book-cover {
    width: 122px;
}
.review .card-block-text .review-text-right {
    margin-left: 144px;
}
.card-block .group-review .backgr-block .card-block-text {
    min-height: 240px;
}
.card-block .review-inner .backgr-block {
    margin: 0 16px;
}
.card-block .group-title.group-title-wide {
    /*line-height: 34px;*/
    padding: 10px 24px;
}

/* полоска прокрутки в профиле */
.group-review .swiper-container.swiper-carousel-selections .swiper-scrollbar, .swiper-container.swiper-smallslider .swiper-scrollbar {
    height: 4px;
    margin-top: 10px;
}

/* рецки в профиле */
.btn-buy-holder-review {
    margin-top: 10px;
    margin-bottom: 6px;
}

/* ЦИТАТЫ */
.quote-card-top {
    padding: 20px 48px 16px 20px;
}
.quote-card-top::after {
    right: -5px;
	top: -10px;}

.quote-card-top__author {
    margin: 18px 0 0;}

.quote-card .lenta-card-bottom {
    padding: 25px 20px 12px;}
.quote-card-bottom__link-book {
    height: 64px;
    margin: 0 24px 6px 0px;
}
.quote-card-top__quote--big p {
    font: 400 20px/26px "Source Sans Pro", sans-serif;
}

.header-card__overlap::after {
    right: 4px;
    top: -5px;
    width: 36px;
    height: 32px;
    border: none;
    border-radius: 0%;
    background-position: center center;
	background-repeat: no-repeat;
background-image: url("data:image/png;base64,");
    }

.header-card__overlap::before {
    position: absolute;
    content: '';
    top: 5px;
    width: 13px;
    height: 13px;
    border: none;
    border-radius: 50%;
}

.header-card__overlap, .header-card__overlap:visited {
    padding-right: 40px;}

.quote-card .btn-add-plus::before, .lifehack-card .btn-add-plus::before, .histories-card .btn-add-plus::before{
    content: 'хочу!';
	background-color: transparent;}
.quote-card .btn-add-plus--add::before, .lifehack-card .btn-add-plus--add::before{
    content: '';
	background-color:#fff;}
.histories-card:nth-child(odd) .btn-add-plus--add::before, .histories-card .btn-add-plus--add::before {
    content: '';
}
.quote-card .btn-add-plus::after, .lifehack-card .btn-add-plus::after{
    background-color: transparent;
}
.histories-card:nth-child(odd) .btn-add-plus:not(.btn-add-plus--add)::before, .histories-card:nth-child(odd) .btn-add-plus:not(.btn-add-plus--add)::after, .histories-card:nth-child(even) .btn-add-plus:not(.btn-add-plus--add)::before, .histories-card:nth-child(even) .btn-add-plus:not(.btn-add-plus--add)::after {
    background-color: transparent/*#EAE9D6*/;
}
.quote-card .btn-add-plus--add::before, .quote-card .btn-add-plus--add::after, .lifehack-card .btn-add-plus--add::before, .lifehack-card .btn-add-plus--add::after, .histories-card:nth-child(odd) .btn-add-plus--add::before, .histories-card:nth-child(odd) .btn-add-plus--add::after {
    background-color: #B2AF8E;
}
.quote-card:nth-child(even) .btn-add-plus--add::before, .quote-card:nth-child(even) .btn-add-plus--add::after, .lifehack-card:nth-child(even) .btn-add-plus--add::before, .lifehack-card:nth-child(even) .btn-add-plus--add::after {
    background-color: #aaa;
}

/*.histories-card .btn-add-plus, .review-card .btn-add-plus, .statuses-card .btn-add-plus, */ .quote-card .btn-add-plus, .lifehack-card .btn-add-plus, .histories-card .btn-add-plus{
    width: 62px;
    border: 1px solid #B2AF8E;
    background-color: #B2AF8E; /*хаки*/
}
.quote-card:nth-child(even) .btn-add-plus, .lifehack-card:nth-child(even) .btn-add-plus, .histories-card:nth-child(even) .btn-add-plus{
    border: 1px solid #aaa;
    background-color: #aaa; /* серый */}

.quote-card .btn-add-plus--add, .lifehack-card .btn-add-plus--add{
    width: 32px;
    border: 1px solid #B2AF8E;
    background-color: #fff;
}
.histories-card .btn-add-plus--add {
    width: 32px;
    background-color: transparent;
    border: 1px solid #B2AF8E;
}
.quote-card:nth-child(even) .btn-add-plus--add, .lifehack-card:nth-child(even) .btn-add-plus--add{
    width: 32px;
    border: 1px solid #aaa;
    background-color: #fff;
}
.histories-card:nth-child(even) .btn-add-plus--add {
    border: 1px solid #aaa;
}
.histories-card:nth-child(even) .btn-add-plus--add::before, .histories-card:nth-child(even) .btn-add-plus--add::after {
    background-color: #aaa !important;
}


/* РЕЦЕНЗИИ */
.review-card .lenta-card-book {
    max-height: 256px;
    /*border-radius: 0px;*/
}
.review-card .lenta-card__rating {
    bottom: 108px;
}
.review-card .lenta-card-book__img {
    border-radius: 0;
}

.review-card .lenta-card__book-title{
    margin: 32px 24px 0 0;
    color: #252626;
}
.review-card .lenta-card__author, .review-card .lenta-card__rating {
	color: #252626;
}
.review-card .lenta-card__rating::before {
	background: url("https://s.livelib.ru/img/skins/ll2019/SVGsprite.svg") -280px 0 no-repeat;
}

.review-card .btn-add-plus, .review-card .btn-add-plus--add {
    border: 1px solid #9b9c9c;
}
.book-rewiews .review-card .btn-add-plus {
    left: 24px;
}
.review-card .btn-add-plus--add::before, .review-card .btn-add-plus--add::after {
    background-color: #9b9c9c !important;
}
.lenta-card-book__bg {
    z-index: -999;
    display: none;
}
.review-card .lenta-card-book::after {
    background: rgba(0, 0, 0, 0.02);
}
.lenta-card__mymark::before {
    opacity: .65;
}
.lenta-card__text p {
    font: 16px/21px "Source Sans Pro", sans-serif;
    color: #252626;
    margin: 0 0 6px;
}



.review-card-top {
    height: 220px;
}
.review-card-top > a > img.review-card-top__img-book {
    height: 218px  !important;
    width: auto !important;
}
.review-card-top__wrapper {
    padding: 20px 16px 20px 0;
}
.lenta-card-bottom {
    padding: 30px 20px 12px;
}

/*.lenta-card__text p br+br {
    display: none;
}*/
.lenta-card__text p br {
   line-height: 30px;
}
/*
.review-card-bottom__text br+br {
    display: none;
}*/
.review-card-bottom__text br {
   line-height: 26px;
}
.review-card-bottom__text p {
   margin-bottom: 10px;
}
.review-card-top__buy {
    bottom: 27px;
    right: 32px;
}
.review-card-top__rating {
    margin: 0 0 32px;
}
/*.review-card .btn-add-plus{
   width: 62px;
}
.review-card .btn-add-plus::before{
    content: 'хочу!';
    color: #3e3f40;
}*/
.review-card .btn-add-plus::before, .review-card .btn-add-plus::after {
    background-color: #9b9c9c;
}
.review-card .btn-add-plus--add{
    width: 32px;
}
.review-card .btn-add-plus--add::before, .review-card .btn-add-plus--add::after{
    content: '';
	background-color:#fff;}
.review-card-bottom__mymark svg {
    fill: #ccab06;} /* цвет звездочки в рецке */

/* правки в ленте друзей */
.carousel-scifi-books .book-data .userbook-container {
    width: 120px;
}
.uelist-wide .brow-cover .cover-wrapper img {
    width: 120px;
    min-width: 120px !important;
    min-height: 170px !important;
   /* margin-left: 10px;*/
}
.blist-biglist .brow .cover-wrapper {
    width: 120px;
}
.blist-biglist .brow .book-data .userbook-container {
    width: 120px;
}

.blist-biglist .brow .brow-cover .brow-rating .rating-value {
    margin: 0 4px 0 0;}
.brow .r00-sc-orange, .brow .r05-sc-orange, .brow .r10-sc-orange, .brow .r15-sc-orange, .brow .r20-sc-orange, .brow .r25-sc-orange, .brow .r30-sc-orange, .brow .r35-sc-orange, .brow .r40-sc-orange, .brow .r45-sc-orange, .brow .r50-sc-orange {
transform: scale(0.92);
}
.blist-biglist .brow .brow-data {
    margin-left: 148px;
}
.brow-details table.compact tr th, table.compact tr td {
    padding: 0px 14px 4px 0;
}

/* ДР авторов */
.born-authors .arow .arow-inner {
    padding: 16px 12px;
}

/* ЛАЙФХАКИ */
.lifehack-card-top__wrapper {
    margin: 22px 22px 16px;
    padding: 22px 26px 30px;
}

/* ИСТОРИИ */
.histories-card-top__wrapper {
    padding: 20px 25px 27px 0;
}
div.histories-card-top {
    height: 260px;
}
.histories-card .lenta-card__text p {
    font-size: 16px;
    line-height: 22px;
}
.histories-card .lenta-card__text p br + br {
    display: none;
}
.histories-card .lenta-card__text p br {
    line-height: 26px;}

.commenttext p + br + br {display:none;}

button.reading-random__button {
    position: relative;
    right: -100%;
    top: -42px;
    transform: translate(-100%);
}

}
