css:
@-moz-document domain("everquest.allakhazam.com") {
a:visited {
    color: #000;
}
.alert > .box {
    background: #c0ad89;
    background-image: none;
}
.bcont, #user-nav li, #video-infobox {
    background: #d7c69a;
    background-image: none;
}
.bcont-dark, .bcont-alt {
    background: #c0ad89;
    background-image: none;
}
.bcont-light {
    background: #d4c7ab;
    background-image: none;
}
.box {
    background: #d4c7ab;
    background-image: none;
}
.box .header {
    background: #c0ad89;
    background-image: none;
}
#col-right {
    background: #c0ad89;
    background-image: none;
}
.datatable {
    background: #d7c69a;
    background-image: none;
}
.datatable caption {
    background: #d7c69a;
    background-image: none;
}
.datatable th {
    background: #b1a67e;
    background-image: none;
}
.datatable tr:hover, .datatable tr.lr:hover, .datatable tr.dr:hover {
    background: #d4c7ab;
    background-image: none;
}
.dr, .s2-dr {
    background: #d7c69a;
    background-image: none;
}
#dialogue-cont .cont {
    background: #d7c69a;
    background-image: none;
}
#featured-promos .cont {
    background: #d4c7ab;
    background-image: none;
}
footer .block-content {
    background: #d4c7ab;
    background-image: none;
}
footer .footer-featured-sites ul li a, footer .footer-zam .footer-social .footer-callout, footer .footer-zam .footer-social-links {
    background: #c0ad89;
    background-image: none;
}
.forums .post-buttons a {
    background-image: none;
}
.forums .post-buttons a.disabled-button {
    background: #d7c69a;
    background-image: none;
}
#forums table, #forum table, #messages .f-table {
    background: #d7c69a;
    background-image: none;
}
#forums th, #forum th, #messages .f-table th {
    background: #b1a67e;
    background-image: none;
}
#forums table, #forum table, #messages .f-table hover {
    background: #d4c7ab;
    background-image: none;
}
#forum tr:hover, #messages .f-table tr:hover {
    background: #c0ad89;
    background-image: none;
}
.gametag {
    background: #c0ad89;
    background-image: none;
}
html, body {
    background: #8a816f;
    background-image: none;
}
li .first-child {
    background-image: none;
}
.lr, .s2-lr {
    background: #d4c7ab;
    background-image: none;
}
#livesearch ul {
    background: #d4c7ab;
    background-image: none;
}
#livesearch a span {
    background: #d4c7ab;
    background-image: none;
}
#livesearch .hlight a, #livesearch li:hlight a span {
    background: #c0ad89;
    background-image: none;
}
.menuh {
    background-image: none;
}
#menu_horiz {
