    background-image: none;
}
#notifications {
    background: #d7c69a;
    background-image: none;
}
.no-template {
    background: #d4c7ab;
    background-image: none;
}
.pages span, .pages a {
    background: #d7c69a;
    background-image: none;
}
.pages span {
    background: #c0ad89;
    background-image: none;
}
.path ul {
    background: #d7c69a;
    background-image: none;
}
.pre-ie9 .post .head .info .nukes > span {
    background: #c0ad89;
    background-image: none;
}
.post .poster {
    background: #c0ad89;
    background-image: none;
}
.post {
    background: #d7c69a;
    background-image: none;
}
.post-inner {
