    background: #c0ad89;
    background-image: none;
}
.post .head, .post.post-filtered {
    background: #c0ad89;
    background-image: none;
}
.post .head .info .nukes > span {
    background: #d7c69a;
    background-image: none;
}
.post .quote {
    background: #d4c7ab;
    background-image: none;
}
.post .quote .source {
    background: #b1a67e;
    background-image: none;
}
#row-top {
    background: #c0ad89;
    background-image: none;
}
.sortable {
    background: #d4c7ab;
}
.tablist a {
    background-image: none;
}
.wiki-nav .cur, .wiki-nav .tools:hover {
    background: #c0ad89;
    background-image: none;
}
.wiki-nav .tools ul {
    background: #c0ad89;
    background-image: none;
}
.wiki-cats {
    background: #b8a480;
    background-image: none;
}
#wrapper {
    background: #d4c7ab;
    background-image: none;
}
}
