css:
@-moz-document domain("ici.tou.tv"), domain("tou.tv") {
.withSideContent,
.playerWrapper.withSideContent .playerWrapper__content-container {
	/* 16:9 / 16:10 are the standard format, computer screen also follow this standard. for working better, not force height value. */
	width:100% !important;
	height:100%;
	max-width:100% !important;
	max-height:100% !important;	
	padding:0 !important;
}

/* Disbale next selections */
#aussi,
.suggestion-lineup {
    display:none;
}
.horizontal-lineup {
    background:#000;
}
.home-lineup .card-emisode .card-description {
    background:#333;
    color:#ddd;
}
.horizontal-lineup h2:before,
.horizontal-lineup .logo img {
    background:#111;
    padding:0.2rem;
    border-radius:0.5rem;
background: linear-gradient(
176deg
,#00a5ad,#00a5ad 10.5%,#00797f);
    background-image: linear-gradient(
176deg
, rgb(0, 165, 173), rgb(0, 165, 173) 10.5%, rgb(0, 121, 127));
    background-position-x: initial;
    background-position-y: initial;
    background-size: initial;
    background-repeat-x: initial;
    background-repeat-y: initial;
    background-attachment: initial;
    background-origin: initial;
    background-clip: initial;
    background-color: initial;    
}
}
