css:
@-moz-document domain("ici.radio-canada.ca"), domain("radio-canada.ca") {
body, .s-lineup-top-story .top-story-container, .m-mega-menu, .g-frontpage-homepage,
.gYrLOy .main-col [class^="item--"],
.gYrLOy,
.fBYbhU,
.fmoBL,
.jxgBll {
    background-color: #000 !important;
}
div.button-content, .g-main-header .e-button-live, .m-sub-navigation-bar-lvl-1 {
    background-color: #999 !important;
}
.card-style, .card, .document-simple-container, .m-continuous-feed .feed-list-container, .m-comment, .m-viafoura, .m-newsletter-subscription .newsletter-subscription-container, .m-navigation-bar, .card-flex, .card-flex .container-col-text .title, .card-without-grid .container-col-text .title, .card-flex .container-col-text .lead, .card-without-grid .container-col-text .lead p {
    background-color: #333;
    color: #eee;
}
.color-txt-dx4, .m-menu-lineup ul .e-external-link, .m-menu-lineup ul .e-internal-link, .m-menu-lineup ul .g-break .break-text-edito a, .g-break .break-text-edito .m-menu-lineup ul a, .m-continuous-feed .feed-list-container .feed-list-date {
    color: #aaa;
}
.e-image-legend.col-sm-6.col-md-10.col-lg-12.col-xl-12.offset-sm-1.offset-md-1.offset-lg-2.offset-xl-4 div p, .document-simple-container span {
    color: #999 !important;
}
.e-textual-logo, .e-title, .e-simple-title-lineup, .e-simple-title-lineup .icon-container, .m-continuous-feed .feed-list-container .feed-list-title, .v-document-simple .m-h1-heading h1, .m-comment .comment-container h2, .e-button-mega-menu, .card-flex:hover, .card-flex:hover .container-col-text .title, .card-without-grid:hover .container-col-text .title, .card-flex:hover .container-col-text .lead, .card-without-grid:hover .container-col-text .lead p, h1 {
    color: #fff;
}
.e-btn:disabled, .e-btn.disabled, .e-btn:disabled:active, .e-btn:disabled:focus, .e-btn:disabled:hover, .e-btn.disabled:active, .e-btn.disabled:focus, .e-btn.disabled:hover, .e-menu-category {
    background-color: #000;
    border-color: #000;
    color: #999;
}
.e-menu-tab {
    background-color: #000;
    border-color: #000;
    color: #eee;
}
.e-menu-tab:hover, .e-menu-tab.is-active {
    background-color: #333;
    border-color: #000;
    color: #eee;
}
.e-btn.btn-secondary, .e-btn.btn-alternative {
    background-color: #333;
    border-color: #000;
    color: #fff;
}
.document-content-style .e-fragment, .document-content-style .framed, .document-content-style .IframeSoundcloud, .document-content-style .m-expandable-html-attachment, .document-content-style .medianet-video-container {
    background-color: #fff;
    padding: 5px;
    border-radius: 3px;
    color: #000;
}
.m-continuous-feed {
    box-shadow: 0 0.2px 5px 1px #666;
}
.s-lineup-top-story .top-story-container li {
    border-color: #222;
}
.redactionals a, .e-internal-link, .g-break .break-text-edito a, .e-anchor-link, .e-breadcrumb-link {
    color: #0191d9;
    border-color: #0191d9;
}
.redactionals a:hover, .e-internal-link:hover, .g-break:hover .break-text-edito a:hover, .e-anchor-link:hover, .e-breadcrumb-link:hover {
    color: #0191d9;
    border-color: #20b5ff;
    background: none;
}
.m-continuous-feed .feed-list-container .feed-list-date-container {
    color: #ff2626;
}
.logo svg {
    background:#666;
    padding:5px;
    border-radius:3px;
}
}
