css:
 @-moz-document domain("www.nitrotype.com")
{
body
  {
    background-color: #2d2e2e;
    overflow-y: scroll;
    background: none;
    overflow-x: hidden;
    position: relative;
  }



html
  {
    background: fixed url(https://imgur.com/WwxAVhl.png) no-repeat, #2d2e2e;
    box-sizing: border-box;
    color: #2d2e2e;
    font-family: "Montserrat", sans-serif;
    font-size: 16px;
    font-weight: 300;
    line-height: 1.6;
  }

  .structure-ad
  {
    display: none;
  }

.structure-ad
  {
    display: none;
  }

  .ad
  {
    display: none;
    position: relative;
    margin-right: auto;
    margin-left: auto;
    text-align: center;
  }

  .ad--sq
  {
    display: none;
    width: 336px;
    height: 280px;
    background: #f2f2f2;
  }

  .ad--ldrbrd
  {
    display: none;
    width: 728px;
    height: 90px;
    background: #f2f2f2;
  }

  .ad--ldrbrd_l
  {
    display: none;
    width: 970px;
    height: 90px;
  }

  .ad--fixed
  {
    display: none;
    position: fixed;
    top: 30px;
  }

  .structure-header
  {
    margin-left: auto;
    margin-right: auto;
    max-width: 1020px;
    padding-left: 0px;
    padding-right: 0px;
  }

.table--b .table-cell
  {
    border-bottom: none;
  }

  .theme--pDefault .profile-bg
  {
    background: url("https://imgur.com/3Deud5U.png") top left repeat, linear-gradient(0deg, #2d2e2e 60%, #ffffff 100%);
    color: #d3d3d3;
  }

  .theme--pDefault .profile-bgHead
  {
    background: url("https://imgur.com/3Deud5U.png") top left repeat, linear-gradient(0deg, #2d2e2e 10%, #ffffff 100%);
  }
   
.theme--pDefault .profile-ad {
    border: none;
}

.theme--pDefault .profile-ad
  {
    display: none;
  }

.profile-car {
 margin: 0;
    padding: 40px 20px;
    text-align: center;
    height: 100%;
    display: flex;
    align-items: center;
    justify-content: center;
}

.card--a
  {
    background-color: #fff0;
  }

  .card--shadow-l
  {
    box-shadow: none;
  }

  .card--grit
  {
    background-image: url("https://imgur.com/F0Z1pvm.png");
  }
.theme--pDefault .profile-body {
    color: #ff0000;
}

.news--solo .news-body p {
    color: #ffffff;
    line-height: 1.8;
}
}
