css:
@-moz-document domain("sta.sh"), domain("deviantart.com") {
/* Dark Default Journal Skin for DeviantArt & sta.sh v1.1 by fffafnir
   https://www.deviantart.com/fffafnir/ | https://www.patreon.com/fffafnir/ */

/* Journal page */
.journal-green, .journal-green .gr-box, .journal-wrapper2 .daeditor-type.journal-green .gr-box {
	background-color: #2c3635;
	color: #fff;
}
.journal-green .gr-top ul li a.u, body.stash-page .ccomment .text a, body.stash-page .journal-wrapper .text a, .journal-green a.u:not(.banned), .journal-green body.gruze a.u:not(.banned), .journal-green .mcb-title a:not(.external), .journal-green .mcb-line a:not(.external), .journal-green .mcb-body a:not(.external), .journal-green .mcb-modhtml a:not(.external), .journal-green .popup2-mcbox-comment a:not(.external):not(.thumb), .journal-green .text a:not(.external) {
    color: #95d0f7;
}
.journal-green a.external, #devskin0 > div > div > div.gr-body > div > div > div.c.ppb.alink.bottom > a, #devskin > div > div > div.gr-body > div > div > div.c.ppb.alink.bottom > a, #devskin > div > div > div.gr-top > div > div > ul > li.category > span > a {
	color: #a6c5b5 !important;
}
.journal-green .gr-top .gr h2 a, .journal-green .crumb a {
    color: #dad8d8;
}
body.stash-page .journal-green h1, body.stash-page .journal-green h2, body.stash-page .journal-green h3, body.stash-page .journal-green h4, .journal-green div.gr-body div.text h1, .journal-green div.gr-body div.text h2, .journal-green div.gr-body div.text h3, .journal-green div.gr-body div.text h4 {
    color: #b2bdbc;
}
.journal-green .gr1, .journal-green .gr2, .journal-green .gr3 {
	-webkit-filter: invert(1);
	filter: invert(1);
}
.journal-green .gr2 {
	margin-right: 0px !important;
}
/* Journal/literature thumbnail */
.thumb .lit .q, .stash-stream a.lit q, .new-stack span.stack-crop div.freeform-wrapper {
	color: #dedede !important;
}
.stash-stream a.lit q strong, .new-stack span.stack-crop div.freeform-wrapper strong:first-child {
	text-shadow: 0 1px 0 rgba(0, 0, 0, .7);
    color: #fff;
}
a.lit, .stash-stream .stash-thumb-container span.shadow .thumb .wrap, .new-stack span.stack-crop div.freeform-wrapper {
	background: -webkit-linear-gradient(top, #0c100a, #1a2216);
	background: -moz-linear-gradient(top, #0c100a, #1a2216);
    background: linear-gradient(top, #0c100a, #1a2216);
	border-color: #000;
	color: #dedede !important;
}
}
