css:
@-moz-document domain("moodle.nottingham.ac.uk") {
:root{
    /* --- Text --- */
    --text: #FAFAFA;
    --heading: #2196F3;
    --link: #03A9F4;
    --visited: #00BCD4;
    /* --- Borders --- */
    --border: #18FFFF;
    /* --- Backgrounds --- */
    --mainBackground: #212121;
    --lists: #303030;
    --gradientLeft: #1A1A1A;
    --gradientRight: #050505;
    --textBox: #424242;
    --button: #009688;
    
}


/* ---------- Overall ----------*/
body{
    background: linear-gradient(to right, var(--gradientLeft) 0%, var(--gradientRight) 100%);
    color: var(--text);
}

#page-header .btn-edit{
    visibility: hidden;
}

#username, #password{
    background: var(--textBox) !important;
    color: var(--text);
}

.singlebutton .btn, #loginbtn{
    background-color: var(--button) !important;
}

.region_main_settings_menu_proxy{
    background: #101010;
}

.uon-content{
    background: #2A2A2A !important;
    border: 1px solid #303030 !important;
}
.modal-content{
    background: var(--mainBackground) !important;
    color: var(--text) !important;
}

/* ---------- Card ----------*/
.content, .node_category {
    background: var(--mainBackground) !important;
}

.card-header{
    border-bottom: none;
}

.card-block, .card-body{
    background: #101010;
}

.card{
    border: 1px solid var(--mainBackground);
}

.bg-primary, .bg-primary:link, .bg-primary:visited{
    background: #2E7D32 !important;
    color: var(--text) !important;
}

a.bg-primary:hover {
    background: #2E7D32 !important;
    
}

.section{
    border-radius: 0 !important;
}

.card-title{
    color: #03A9F4 !important;
}

.no-overflow, .no-overflow>p, .popover{
    background: var(--mainBackground) !important;
}


