/* ---------- Navbar ----------*/
.navbar-light, .navbar-nottingham, .navbar{
    background: rgba(50,50,50,0.95) !important;
    border-bottom: 1px solid var(--border);
}

#block-new-course-overview .nav-link{
    color: var(--text) !important;
    border: 1px solid #9E9E9E !important;
    border-bottom: 1px solid white !important;
}

#block-new-course-overview .nav-link:focus, #block-new-course-overview .nav-link:hover, #block-new-course-overview .nav-link.active{
    background-color: #424242 !important;
    border: 1px solid var(--border) !important;
    border-bottom: 1px solid white !important;
    
}

.nav-tabs .nav-link.active {
    background-color: var(--mainBackground);
}

.page-heading, .sectionname, h1 {
    background: #101010 !important;
    border-bottom: none !important;
}

.theme-slider{
    visibility: hidden !important;
}

/* ---------- Textual Elements ----------*/
h1, h2, h3, h4, h5, h6{
    background: none !important;
    color: #26C6DA !important;
}

a:link{
    color: var(--link) !important;
}

a:visited{
    color: var(--visited) !important;
}

span, a{
    color: var(--text) !important;
}

table, tr, p{
    background: var(--mainBackground) !important;
    color: var(--text) !important;
}

.instancename {
    color: var(--heading) !important;
}

.form-control{
    background: var(--textbox) !important;
    color: var(--text) !important;
}

/* ---------- Text - Graphics being different ---------- */
[style="background-color: rgb(245, 246, 245);"]{
    color: var(--text) !important;
    background: var(--mainBackground) !important;
}

pre{
    color: var(--text) !important;
    background: var(--mainBackground) !important;
}




/* ---------- Search ----------*/
#search{
    background: #424242;
    color: var(--text);
}

#search:focus{
    border-color: var(--border);
}

/* ---------- Dropdown menu ----------*/

.dropdown-menu {
    background: var(--mainBackground);
    border: 1px solid #424242;
}

.dropdown-item{
    border-bottom: none !important;
}

.dropdown-item:focus{
    background-color: #424242;
    border: 1px solid var(--border) !important;
}

.dropdown-item:hover {
    background: #555;
}

.custom-select{
    background: #424242;
    color: var(--text);
}

/* ---------- Menu ----------*/
.list-group-item, #nav-drawer, #nav-drawer .nav-item{
    background-color: var(--lists) !important;
    border-bottom: none !important;
    border: 1px solid var(--mainBackground) !important;
}

.list-group, #nav-drawer>nav, .dropdown-toggle{
   border: none !important;

}
/* ---------- Nav ----------*/
.nav-right>div{
    background: #424242 !important;
}


/* ---------- Section ----------*/
.sectionbody{
    background: #101010 !important;
}

.sectionhead{
    background: #424242 !important;
}

.sectionhead h3{
    background: #424242 !important;
}

/* ---------- Activity ----------*/
.activity_overview{
    background: #101010;
}

/* ---------- Forums ----------*/
.forumpost{
    background: #101010;
}

/* ---------- Footer ----------*/
.copyright{
    background: #101010;
}

#page-footer, section.edge--bottom{
    background: var(--mainBackground) !important;
}

/* ---------- Grades ---------- */

th, td{
    background-color: var(--mainBackground) !important;
}

/* ---------- Filemanager ---------- */
.filemanager-container, .fp-pathbar, .fp-navbar{
    background-color: var(--mainBackground) !important;
}

#id_cancel{
    background: #00695C !important;
}

#id_cancel:hover{
    background: #004D40 !important;
}

.fp-toolbar a{
    background: #8BC34A;
}

.fp-toolbar a:hover{
    background: #558B2F;
}

/* ----------- Messages & Search ---------- */
.message-drawer>div, .message-drawer .card, .message-drawer .d-flex{
    color: var(--text);
    background: var(--mainBackground) !important;
}

.input-group>input, .input-group-text{
    background: var(--mainBackground) !important;
    border: 1px solid #303030 !important;
}

/* ---------- Notifications ---------- */
.popover-region-header-container, .popover-region-container, .popover-region-footer-container{
    background: var(--mainBackground) !important;
    border: 1px solid #303030 !important;
}


}
