css:
@-moz-document domain("trends.gab.com") {
:root {
    --darker-bg: #101010;
    --dark-bg: #303030;
    --text-light: #c0c0c0;
    --a-color: #04abe2
}

body.hydra {
    background-color: var(--dark-bg);
}

body {
    color: var(--text-light);
    background-color: var(--dark-bg);
}

a {
    color: var(--a-color);
}

.hydra-menu-item, ml-auto {
    color: var(--a-color);
}

.text-light {
    color: var(--darker-bg) !important;
}

.card {
    background-color: var(--darker-bg);
}
}
