css:
@-moz-document domain("email.fit.vutbr.cz") {
/*
#202225
#2b2d31
#36393f
#464a53
#636874
*/
body {
    color:white;
    background: #464a53;
}

.header, #menu {
    background: #202225;
}

/* table */
/* header text color */
.widget {
    color: white;
}
/* header background color */
.mboxcontrol{
    background: #2b2d31;
}
/* from and subject text color */
a {
    color: #fff;
}
/* email table header background */
.item {
    color: white;
    background: #36393f;
}
/* messages background */
.seen, .unseen {
    background:#464a53;
}
.answered {
    background:#4e834e;
}
.seen:hover, .unseen:hover {
    background:#666666;
}
.answered:hover {
    background:#609f60;
}
/* table border */
.messageList th, .messageList td {
    border: 1px solid black;
}
.selected {
    background:#7480a7;
}
}

@-moz-document url-prefix("https://email.fit.vutbr.cz/horde/imp/message.php") {
/* Date and Recipient lines background color */
.item0 {
    background-color: #2b2d31;
}
/* From and Subject lines background color */
.item1 {
    background-color: #36393f;
}
/* link color */
td a {
    color: #8a8aff;
}
/* message window body color */
.messageBody {
    background: #636874;
}
/* message body color */
.text {
    background: #636874;
}
}
