css:
@-moz-document url-prefix("http://127.0.0.1:8000/admin/"), url-prefix("http://localhost:8000/admin/"), url-prefix("http://127.0.0.1:9000/admin/"), url-prefix("http://localhost:9000/admin/")
{
 /*start style*/
#branding h1,
#branding h1 a:link,
#branding h1 a:visited {
    color: #ff7c00!important;
}

body,
.module,
.actions,
#changelist-filter {
    background: #38383d!important;
    color: #b1b1b3!important;
}

.paginator {
    background: #38383d!important;
    border: none !important;
}

.login #container {
    background: #38383d!important;
    border: none !important;
}

#login-form .submit-row {
    background: #38383d !important;
}

.button,
input[type="submit"] {
    background: #4d4d64 !important;
}

#header,
.breadcrumbs,
.module h2 {
    background: #4d4d64!important;
}

.module caption {
    background: #4d4d64!important;
}

#toolbar {
    background: #38383d!important;
    border: none !important;
}

#changelist #toolbar form input[type="submit"],
#changelist .actions .button {
    border: none !important;
    box-shadow: none !important;
    color: #b1b1b3 !important
}

.module h3 {
    background: #434355 !important;
    border: none !important;
    color: #9b9b9b !important;
}

label.required,
th.required {
    color: #a2a2a2 !important;
}

fieldset {
    background-color: #38383d !important;
}

fieldset .form-row {
    border-bottom: 1px solid #484646 !important;
}

table,
table th,
table td {
    border: none !important;
}

thead th {
    background: #2c2c2f !important;
}

.row1 {
    background-color: #38383d !important;
}
.row2 {
    background-color: #434348 !important;
}

.inline-group .tabular tr.add-row td,
.submit-row {
    background: #2c2c2f !important;
    border: none;
}
    
     /*end style*/
}
