css:
@-moz-document domain("methodscript.com") {
html,
body {
    min-height: 100%;
    margin: 0;
    background-color: #23272A;
}
#header,
#footer {
    background-color: #23272A;
    color: #FFF;
}
#skel-layers-wrapper {
    background-color: #2C2F33;
    color: #FFF;
}
#header nav > ul > li a {
    color: #8eb06d;
}
#edit_this_page {
    color: #FFF;
}
#edit_this_page a {
    margin-left: 0.5em;
}
a {
    color: #82c341;
    text-decoration: none;
}
a:active, a:hover {
    color: #b1dfa8;
}
footer a {
    text-decoration: none;
}
footer a:active, #footer a:hover {
    color: #b1dfa8;
}
strong, b {
    color: #9ee15b;
}
h1, h2, h3, h4, h5, h6 {
    color: #82c341;
}
.redLink {
    color: #c11515;
}

table.wikitable tbody tr th:empty {
    display: none;
}
table th {
    color: #b3e482
}
table strong, table b {
    color: #82c341;
}
table div[style*="background-color: green; font-weight: bold; text-align: center;"] {
    background-color: #82c341 !important;
}

.sb-icon-search {
	background: #2b2f32 !important;
}
.sb-icon-search-normal,
.sb-icon-search-close {
	filter: invert(100%);
}
.sb-search-results {
	border: none;
}

/* Logo header */
#header h1 {
	left: 3.5em;
}

#header > img:first-child {
	margin-bottom: -1em;
	margin-left: .5em;
}

/* Logo frontpage */
img[alt="Image:CommandHelper_Logo_New.png"] {
    display: block;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    background: url(https://i.imgur.com/4TtTfgg.png) no-repeat;
    width: 381px; /* Width of new image */
    height: 90px; /* Height of new image */
    padding-left: 381px; /* Equal to width of new image */
}

/* Scroll to top button */
button[data-id="scroll-to-top-button"] {
    background-color: #82c341 !important;
    box-shadow: rgba(90, 130, 52, 0.3) 2px 3px 8px 2px !important;
}
button[data-id="scroll-to-top-button"]:hover {
    box-shadow: rgba(90, 130, 52, 0.3) 4px 6px 16px 4px !important;
}
}

@-moz-document domain("methodscript.com") {
/* Notes & Warnings */
.TakeNote {
    border-top: 2px solid #5c8350;
    border-bottom: 2px solid #5c8350;
    box-shadow: 0 .3em .3em rgba(0, 0, 0, 0.8);
}
.Warning {
    border-top: 2px solid #dfb800;
    border-bottom: 2px solid #dfb800;
    box-shadow: 0 .3em .3em rgba(0, 0, 0, 0.8);
}
.Warning strong, .Warning b {
    color: #dfb800;
}

/* Code blocks */
.methodscript_code {
    background-color: #23272A !important;
    color: #bfbfbf;
    border-color: #5c8350 !important;
    border-style: solid;
    border-width: 1px 0px 1px 0px;
}
.methodscript_code * {
    font-style: normal !important;
}
.methodscript_code .function {
    color: #bf0 !important;
}
.methodscript_code .single_string {
    color: #ffca00 !important;
}
.methodscript_code .double_string {
    color: #ffca00 !important;
}
.methodscript_code .double_string span {
    color: #0095ff !important;
}
.methodscript_code .var {
    color: #f43e00 !important;
}
.methodscript_code .comment {
    color: #525552 !important;
}
.methodscript_code .command {
    color: #ff29d7 !important;
}
.methodscript_code .keyword {
    color: #9a46f0 !important;
}
.methodscript_code .object_type {
    color: #9ecca1 !important;
}
.methodscript_code div span[style*="font-style: italic; color: #BDC4B1;"] {
    font-style: italic !important;
    color: #BDC4B1 !important;
}

dl dd span { /* Used for weird comments */
    color: #43c42d !important;
}

/* Other code blocks */
.pre {
    background-color: #23272A !important;
    color: #bfbfbf;
    border-top: 1px solid #646464;
    border-bottom: 1px solid #646464;
    font-style: normal !important;
}

.pre[style*="border-top: 1px solid blue; border-bottom: 1px solid blue; background-color: #BDC7E9;"] {
    border-top: 1px solid #5a5acc !important;
    border-bottom: 1px solid #5a5acc !important;
    /*background-color: #27273a !important;*/
    background-color: #1f252b !important;
}
}

@-moz-document domain("methodscript.com") {
/* Highighter.js Obsidian theme */
.hljs {
    display: block;
    overflow-x: auto;
    padding: .5em;
    background: #23272A
}
.hljs-keyword,
.hljs-selector-tag,
.hljs-literal,
.hljs-selector-id {
    color: #93c763
}
.hljs-number {
    color: #ffcd22
}
.hljs {
    color: #e0e2e4
}
.hljs-attribute {
    color: #668bb0
}
.hljs-code,
.hljs-class .hljs-title,
.hljs-section {
    color: white
}
.hljs-regexp,
.hljs-link {
    color: #d39745
}
.hljs-meta {
    color: #557182
}
.hljs-tag,
.hljs-name,
.hljs-bullet,
.hljs-subst,
.hljs-emphasis,
.hljs-type,
.hljs-built_in,
.hljs-selector-attr,
.hljs-selector-pseudo,
.hljs-addition,
.hljs-variable,
.hljs-template-tag,
.hljs-template-variable {
    color: #8cbbad
}
.hljs-string,
.hljs-symbol {
    color: #ec7600
}
.hljs-comment,
.hljs-quote,
.hljs-deletion {
    color: #818e96
}
.hljs-selector-class {
    color: #A082BD
}
.hljs-keyword,
.hljs-selector-tag,
.hljs-literal,
.hljs-doctag,
.hljs-title,
.hljs-section,
.hljs-type,
.hljs-name,
.hljs-strong {
    font-weight: bold
}
}
