css:
@-moz-document domain("forum.maniaplanet.com") {
/* Container */
body,
.copyright {
    color: #4b799d;
}

.wrap {
    background: #141f2d;
}

/* Navigation */
.navbar {
    background: #1c304a;
}

#nav-breadcrumbs {
    border-top-color: #0095ff;
}

h1,
h2,
h3 {
    color: #0095ff;
}

h3,
hr {
    border-color: #0095ff;
}

/* Hyperlinks */
a,
.post:target h3 a {
    color: #c7eaff;
}

a:hover {
    color: #FFF;
}

/* Forum rules */
div.rules {
    background-color: #f9cd4d;
}

div.rules a {
    color: #000;
}

/* Buttons */
.button,
.pagination li a {
    color: #FFF;
    background: #0095ff;
    border: 1px solid transparent;
    -webkit-box-shadow: 0 0 0 1px transparent inset;
}

.button:hover {
    color: #0095ff;
    background: #FFF;
    border-color: transparent;
    -webkit-box-shadow: 0 0 0 1px transparent inset;
    text-shadow: none;
}

.pagination li a:hover {
    color: #0095ff;
    border-color: transparent;
}

.pagination li.active span {
    color: #0095ff;
    background: #FFF;
    border-color: transparent;
    -webkit-box-shadow: 0 0 0 1px transparent inset;
    text-shadow: none;
    font-weight: 600;
}

.button .icon {
    color: #FFF;
    border: none;
}

.button:hover .icon,
.pagination li a:hover .icon {
    color: #0095ff;
    border: none;
}

.icon-gray {
    color: #006999 !important;
}

.icon-red {
    color: #ee5c97 !important;
}

/* Dropdown menu */
.dropdown .dropdown-contents {
    background: #005799;
    border-color: #068bea;
}

.dropdown .pointer {
    border-color: #068bea transparent;
}

.dropdown .pointer-inner {
    border-color: #005799 transparent;
}

.dropdown li.separator {
    border-color: #068bea;
}

.dropdown-extended .header {
    color: #FFF;
    background: #005799;
    border-color: #068bea;
    text-shadow: none;
}

.dropdown-extended ul li {
    border-top-color: #068bea;
}

.dropdown-extended .footer {
    border-color: #068bea;
}

.jumpbox-sub-link {
    background-color: #005799;
}

.jumpbox-sub-link:hover {
    background-color: #068bea;
}

.jumpbox .dropdown li {
    border-top-color: #068bea;
}

/* Search box */
.search-box .inputbox {
    color: #FFF;
    background-color: #005799;
    border-color: #068bea;
}

/* Tabs */
.tabs .tab > a {
    color: #FFF;
    background: #152842;
}

.tabs .activetab > a {
    color: #FFF;
    background: #1f3451;
    border: none;
    box-shadow: none;
}

/* Forum lists */
.forabg,
.forumbg {
    background-color: #FFF;
    background-image: linear-gradient(to bottom, #86efff 0%, #068bea 2px, #006999 92px, #005799 100%);
}

ul.forums li.row {
    background: #1f3451;
}

ul.forums li.row:nth-child(odd) {
    background: #1c304a;
}

ul.forums li.row:hover {
    background: #005799;
}

ul.topiclist li {
    color: #4b799d;
}

.bg1 {
    background: #1f3451;
}

.bg2 {
    background: #1c304a;
}

li.row {
    border-color: transparent;
}

li.row strong {
    color: #068bea;
}

li.row:hover {
    background-color: #005799;
}

ul.topiclist dd,
ul.topiclist:hover dd {
    border-color: transparent;
}

table.table1 tbody tr {
    border-color: transparent;
}

table.table1 tbody tr:hover {
    background-color: #005799;
}

table.table1 tbody td {
    border-color: transparent;
}

/* Posts */
.postbody {
    color: #4b799d;
}

.content,
.post:target .content {
    color: #FFF;
}

.signature {
    border-top-color: #0095ff;
}

.postprofile strong {
    color: #0095ff;
}

.postprofile {
    color: #4b799d;
    border-left-color: #0095ff;
}

/* Code blocks */
.codebox {
    background: #111;
    border-color: #005799;
}

.codebox p {
    border-bottom-color: #005799;
}

.codebox code {
    color: #86efff;
}

/* Quote blocks */
blockquote,
blockquote blockquote blockquote {
    color: #FFF;
    background-color: #005799;
    border-color: transparent;
}

blockquote blockquote {
    background-color: #006999;
}

/* Posting box */
.panel {
    background: #1c304a;
}

.panel-container h2 {
    color: #0095ff;
}

.panel-container .panel {
    color: #FFF;
    background: #005799;
}
.panel-container .panel li.row {
    border-color: transparent;
}

.navigation a {
    color: #c7eaff;
    background: transparent;
}

.navigation a:hover {
    color: #FFF;
    background: #005799;
}

.navigation .active-subsection a {
    color: #FFF;
    background: #0095ff;
}

/* Profile */
dl.details dt {
    color: #0095ff;
}

.cp-mini {
    background: #005799;
}

dl.mini dt {
    color: #0095ff;
}
}
