css:
@-moz-document domain("aterdracon.net") {
body{
    background: #000;
}
:root{
    scrollbar-color: red #000;
}
.cb-section-top{
    background: rgba(0, 0, 0, .42);
}
#cb-top-menu{
    background: #000;
}
.cb-light{
    background: transparent;
}
article{
    background: #00000075;
}
.cb-light h2 a,.cb-byline a{
    color: red;
}
.cb-byline .cb-byline-element,.cb-excerpt{
    color: #fff;
}
/*ARTYKUŁ*/
.cb-breadcrumbs{
    background: #000;
}
.cb-fis-tl-default .cb-fis-big .cb-entry-header .cb-title-fi{
    background: transparent;
}
.cb-fis-big .cb-entry-header .cb-title-fi .cb-entry-title{
    color: red;
}
.cb-entry-content p{
    color: #fff;
    text-align: center;
    max-width: 720px;
    margin-left: 15px;
}
.cb-entry-content h3{
    margin-left: 15px;
}
#cb-content{
    background: transparent;
}
div.sharedaddy h3.sd-title,#jp-relatedposts h3.jp-relatedposts-headline{
    color: red;
    margin-left: 15px;
}
#jp-relatedposts .jp-relatedposts-items .jp-relatedposts-post{
    background: #0000007d;
    height: 90px;
    margin: 0 0 1rem 2px;
}
.cb-tags a{
    background: #000;
    border: 3px solid red;
    color: red !important;
}
.cb-beside .cb-title-subtle{
    color: red;
}
#cb-content .cb-sidebar,.cb-post-footer-block{
    background: transparent;
}
.cb-sidebar-widget .cb-sidebar-widget-title{
    background: #26262B;
    color: red;
}
#cb-previous-next-links #cb-previous-link,#cb-previous-next-links .cb-empty{
    background: #000;
}
#cb-previous-next-links #cb-previous-link{
    border-right: 2px solid red;
}
#cb-next-link{
    background: #000;
}
#cb-previous-next-links #cb-next-link{
    border-left: 2px solid red;
}
.cb-block-title{
    color: red;
    border: none;
}
#cb-m-trigger{
    background: red;
}
#cb-author-box .cb-meta .cb-info .cb-author-title a{
    color: red;
}
/*COOKIE*/
#cookie-law-info-bar,#cookie-law-info-again{
    background: #000 !important;
}
#cookie-law-info-bar span,#cookie-law-info-again span{
    color: red;
}
#cookie_action_close_header{
    border: 5px solid red;
}
/*O NAS*/
.cb-cat-header{
    background: transparent;
}
.cb-cat-header #cb-cat-title{
    color: red;
}
.entry-content{
    max-width: 720px;
    margin: 0 15px;
}
.entry-content blockquote{
    border-left: 3px solid red;
}
.entry-content blockquote p{
    color: #c6c6c6;
}
.entry-content p,.entry-content ul li{
    color: #fff;
}
.entry-content{
    color: red;
}
footer:before{
    content: "Ciemny motyw by Maciejozaman";
    color: red;
}
}
