css:
@-moz-document domain("nga.cn"), domain("ngabbs.com"), domain("nga.178.com"), domain("178.com") {
body{background: #e6ecf0;}
button {background: #e6ecf0;}
textarea, select, input {border: 1px solid #d8e0e6;background: #ffffff;}
input[type="checkbox"] , input[type="radio"] {color:#0084B4}
button {background: #e1e8ed;}

.stdbtn {background:#F5F8FA;}
.stdbtn a {border-left:1px solid #e1e8ed;background:#F5F8FA;}
.stdbtn a:hover {background-color:#ffffff;text-decoration:none}
.stdbtn .invert {background:#0084B4;color:#ffffff;}
.stdbtn .invert:hover {background:#0084B4;color:#fff}
.collapse_content {border-top:1px solid #d8e0e6;border-bottom:1px solid #d8e0e6}
.collapse_btn {border-top:1px solid #d8e0e6;border-bottom:1px solid #d8e0e6;}
.postbox .avatar, .msgread .avatar {border:0.0833em solid #0084B4}
.c2 .hsdeck .bg0 {background:#ffffff}
.c1 .hsdeck .bg0 {background:#F5F8FA}

.postbtnsc {border: 1px solid #cacaca;}
#m_posts .row2 .postbtnsc .stdbtn a {background-color:#F5F8FA}
#m_posts .row2 .postbtnsc .stdbtn a:hover {background-color:#0084B4}

#mainmenu .stdbtn .innerbg {background:#F5F8FA}
#mainmenu .stdbtn a:hover {border-bottom:4px solid #0084B4;}
.mmdefault {color:#0084B4}
.mmdefault:hover {color:#657786}

.nav_link {color:#0084B4}
.nav_link:hover {color:#657786}
.nav_root, .nav_link, .nav_spr {background:#F5F8FA;}    
.nav_root {background:#0084B4;border-color:#0084B4}

.catenew .b4 {background-color:#fcfcfc}
.catenew .b3 {background-color:#F5F8FA}
.catenew .b2 {background-color:#fcfcfc}
.catenew .b1 {background-color:#fafafa}
.catenew .b0 {background-color:#ffffff}
.catenew .invert {background-color:#0084B4;}
.catetitle {color:#0084B4;}
.cateblock .contentBlock, .catenew .contentBlock {background-color:#fcfcfc}
#m_cate5 .catenew h2 {padding:0 0.7em;font-size:1.5em;height:36px;line-height:32.4px;background:#fcfcfc}

.forumbox h2,.forumbox h1 {color:#0084B4}
.forumboxrightlist .box h2, .forumbox th, .forumbox thead td {background:#0084B4;}
.row1c1, .forumbox .row1 .c1 {background-color:#fcfcfc}
.forumbox .row1 .c2 {background-color:#ffffff}
.row1 .c2 .posterInfoLine {background-color:#fcfcfc;border-bottom:1px solid #fffcee}
.forumbox .row1 .c2 .bgtxt {color:#F5F8FA} 
.forumbox .row1 .c3 {background-color:#fcfcfc}
.forumbox .row1 .c4 {background-color:#ffffff}
.forumbox .row1 .c5 {background-color:#fcfcfc}
.forumbox .row1 .c6 {background-color:#ffffff}
.forumbox .row1 .c7 {background-color:#fcfcfc}
.row2c1, .forumbox .row2 .c1 {background-color:#e1e8ed}
.forumbox .row2 .c2 {background-color:#F5F8FA}
.row2 .c2 .posterInfoLine {background-color:#e1e8ed;border-bottom:1px solid #fcfcfc}
.forumbox .row2 .c2 .bgtxt {color:#ffffff} 
.forumbox .row2 .c3 {background-color:#e1e8ed}
.forumbox .row2 .c4 {background-color:#F5F8FA}
.forumbox .row2 .c5 {background-color:#e1e8ed}
.forumbox .row2 .c6 {background-color:#F5F8FA}
.forumbox .row2 .c7 {background-color:#e1e8ed}
.forumbox .set_topic .c1 {border-left:0.25em solid #AAB8C2}
.forumbox .set_topic_elm .c1 {border-left:0.5em solid #AAB8C2}
.forumbox .topicrow .topic_content .postcontent {border-top:1px solid #AAB8C2;}
.forumbox .contentBlock {text-align:left;padding:6px;background:#fcfcfc}

.small_colored_text_btn,.block_txt {background: #0084B4;}
.block_txt_big {background: #e1e8ed}
.block_txt_c0 {background: #0084B4;}
.block_txt_c1 {background: #AAB8C2;}
.block_txt_c2 {background: #d8e0e6;}
.block_txt_c3 {background: #e1e8ed;color: #888;}

.quote {background:#fcfcfc;}
table.quote, .dice {background:#d8e0e6;color:#0084B4;}


.single_ttip2 {border:1px solid #0084B4; background:#ffffff;}
.single_ttip2 .div2 {background:#fcfcfc;}
.single_ttip2 .tip_title {background-color:#fcfcfc;border-bottom:1px solid #F5F8FA;color:#0084B4;}
.single_ttip2 .colored_text_btn {background-color:#F5F8FA;color:#0084B4;}
.single_ttip2 .colored_text_btn:hover {background-color:#0084B4;color:#F5F8FA;}

.ubbcode .headline .row1 .b1, .ubbcode .headline .row2 .b2 {background-color:#fafafa}
.ubbcode .headline .row1 .b2, .ubbcode .headline .row2 .b1 {background-color:#ffffff}
.ubbcode .headline .headlinesel .nosel {background:#e6ecf0}
.ubbcode .headline .headlinesel .seled {background:#0084B4}

.haveCustomBackground #m_pbtntop .stdbtn {background:#e1e8ed;}
}
