css:
@-moz-document domain(vk.com) {

body > *, span, button, input {
    letter-spacing: 0.3px;
}

body {
    padding-bottom: 20px !important;
}

.top_profile_img {
    display: none;
}

.top_profile_name {
    padding-right: 0;
}

.top_audio_play {
    display: none;
}

#profile_short .profile_info_row .labeled a:first-letter, .header_right_link:first-letter, .profile_online_lv:first-letter {
    text-transform: uppercase;
}

#profile_short .profile_info_row .labeled a[href^="/away.php"]:first-letter {
    text-transform: lowercase;
}

#profile_short .profile_info_row .labeled a {
    display: inline-block;
}

.page_name {
    font-weight: 700 !important;
}

#stl_left {
    width: 0 !important;
}

#stl_side {
    display: none !important;
}

#side_bar {
    top: 0 !important;
    position: relative;
}

.left_menu_nav_wrap {
    border-top: none;
    width: 149px;
    text-align: center !important;;
}

.left_menu_nav_wrap a:nth-child(2) {
    padding-right: 0;
}

.left_menu_more {
    display: block;
    padding-right: 10px;
}

.ui_actions_menu {
    text-align: left;
}

.blog_right_menu_nav {
    margin-top: 10px;
}

.blog_right_menu .left_menu_nav_wrap{
    margin:0 16px;
    width: auto;
}

.blog_right_menu .left_menu_nav_wrap a:nth-child(3) {
    padding-right: 0;
}

.blog_right_menu .left_menu_nav_wrap a:nth-child(2) {
    padding-right: 10px;
}

#index_fbcontinuewithsign {
    height: 0 !important;
}

#side_bar_inner a[href^="/vkpay"] .left_label {
    font-size: 0;
}

#side_bar_inner a[href^="/vkpay"] .left_label:before {
    content: 'Кошелёк';
    font-size: 12.5px;
}

.olist_item_no_vkpay .olist_item_name {
    font-size: 0;
}

.olist_item_no_vkpay .olist_item_name:before {
    content: 'Кошелёк';
    font-size: 13px;
}

#ads_left, .ads_ad_box_border, #ads_left.ads_left_empty, #lovina_ads_row {
    display: none !important;
    height: 0 !important;
    position: absolute !important;
    top: 0 !important;
    left: -1000px !important;
}

/*[[Scaling]]*/
/*[[Menu]]*/
/*[[Block]]*/
/*[[HideComment]]*/
/*[[HideGroup]]*/
/*[[HideRecommend]]*/
/*[[HideChat]]*/
/*[[Covid19]]*/
/*[[NewLogo]]*/
}

style_setting_options.value:
#side_bar_inner div.more_div {
    display: none;
}
.left_menu_nav_wrap {
    padding-top: 0 !important;
}
#ads_left.ads_left_empty+.left_menu_nav_wrap {
    padding-top: 0 !important;
    border-top: 0 !important;
}
#side_bar_inner ol > div:nth-child(12), #side_bar_inner ol > div:nth-child(18) {
    display: block;
    margin-left: 0 !important;
}
[dir] #side_bar ol {
    margin: 0 0 5px !important;;
}

#side_bar_inner {
    background:#fff;
    border-radius: 2px;
    box-shadow: 0 1px 0 0 #d7d8db, 0 0 0 1px #e3e4e8;
    margin-top: 57px;
}

#side_bar .left_fixer {
    padding:0 15px 0 20px;
}

#side_bar .left_icon {
    display: none;
}

#side_bar ol li {
    margin-left: 0;
}

#side_bar .more_div {
    margin-left: 0;
    margin-right: 0;
    border-top: 1px solid #e7e8ec;
}

#side_bar ol {
    margin: 0;
}

#side_bar ol li .left_row:hover {
    background:#f0f2f5;
}
.left_menu_nav_wrap {
    position: absolute;
    border-top: none;
    margin: 0 8px;
    width: 133px;
    text-align: center;
}
#side_bar .more_div {
    margin-left: 0;
    margin-right: 0;
    border-top: 1px solid #e7e8ec;
}

.more_div.l_main {
     display: none !important;
}

#ads_left.ads_left_empty+.left_menu_nav_wrap {
    border-top: 0 !important;
}

#groups_filters_wrap, #feed_filters div.page_block:nth-child(2), #friends_possible_block, .feed_friends_recomm {
    display: none !important;
}
#ui_rmenu_recommended, #ui_rmenu_search, #ui_rmenu_find, #ui_rmenu_events, #ui_rmenu_category0, .audio_subscribe_promo, .fb-login-button, #top_switch_lang {
    display: none;
}
#narrow_column > div.page_block.ui_rmenu.ui_rmenu_pr._ui_rmenu_auto_expand:first-child > div.ui_rmenu_sep:nth-child(4) {
     display: none;
}

.top_home_logo {
    background: url("data:image/svg+xml;base64,") no-repeat 50%/contain !important;
    width: 24px !important;
    height: 24px !important;
    margin: 9px 0 0 0 !important;
}
.top_home_logo:before {
    content: "ВКонтакте";
    font-size: 14px;
    font-weight: 500;
    color: #fff;
    margin-left: 31px;
    line-height: 25px;
}
.top_home_link:hover {
    text-decoration: none;
}
