css:
@-moz-document domain("app.slack.com") {
[lang] body, [lang] .c-texty_input {
    font-family: monospace;
}
body, .p-channel_sidebar--classic_nav, .p-classic_nav__model__title__name, [lang] .c-message, [lang] .c-texty_input .ql-editor, [lang] .c-texty_input .ql-placeholder, .p-classic_nav__model__title__name__status--im {
    font-size: 13px;
}
.p-workspace--classic-nav {
    grid-template-rows: min-content 40px auto;
}
.p-classic_nav__model__buttons, .p-classic_nav__right_header, .p-channel_sidebar__static_list >[role=presentation], .c-texty_input__button {
    display: none;
}
.p-classic_nav__model__title__info {
    display: inline-flex;
}
.p-channel_sidebar, .p-classic_nav__team_header {
    width: 180px;
}
.p-channel_sidebar {
    width: 180px;
    margin-top: 5px;
}
.p-workspace--context-pane-collapsed {
    grid-template-columns: 180px auto;
}
.p-channel_sidebar__navigation_bar .p-channel_sidebar__jumper {
    width: 156px;
    padding: 1px 4px 1px 4px;
}
.p-classic_nav__team_header__team__name {
    font-size: 15px;
    font-weight: 400;
}
.p-channel_sidebar__channel--unread:not(.p-channel_sidebar__channel--muted):not(.p-channel_sidebar__channel--suggested) .p-channel_sidebar__name, .p-channel_sidebar__link--unread .p-channel_sidebar__name, .p-classic_nav__model__title__name, .p-message_pane__unread_banner__close {
    font-weight: 400;
}
.c-icon {
    transform: scale(.75);
}
.p-classic_nav__team_header__user {
    font-size: 10px;
}
.p-workspace__input .p-message_input_file_button {
    left: 2px;
    bottom: 2px;
    height: 28px;
    width: 28px;
}
.p-workspace__input .p-message_input_field .ql-placeholder {
    left: 30px;
}
.p-workspace__input .p-message_input_field {
    padding-bottom: 5px;
}
.c-texty_input--multi_line[data-min-lines="1"] .ql-editor {
    min-height: 18px;
}
.p-workspace__input .p-message_input_field {
    min-height: 35px;
}
.p-workspace__input .p-message_input_field .ql-editor {
    padding-bottom: 2px;
    padding-top: 3px;
}
.p-classic_nav__channel_header {
    height: 40px;
    padding: 10px 8px 10px 20px;
}
[lang] .c-message_list__day_divider__label {
    font-size: 12px;
    font-weight: 500;
}
.c-message__sender, .p-message_pane__unread_banner__jump, .p-message_pane__unread_banner__msg {
    font-weight: 700;
}
}
