css:
@-moz-document domain("github.com") {
.Header, .bg-gray, .bg-gray *, .dashboard-sidebar, .dashboard-sidebar *, .user-profile-link, .dropdown-menu, .dropdown-menu *, .user-profile-sticky-bar:after {
    background-color: black !important;
    color: white !important;
}

.Box, .border-bottom, .form-control, .form-select {
    border-color: #1e272e !important;
    border-radius: 20px;
}

.border-bottom, .border-top {
    border-color: #1e272e !important;
}

.btn {
    background-color: black !important;
    color: #1e272e !important;
    border-radius: 20px;
    background-image: none;
}

.application-main, .application-main *, .logged-in, .logged-in * {
    background-color: black !important;
    color: white !important;
}

.tabnav-tab.selected, hr, li, .list-group-item, .boxed-group-inner {
    border-color: #1e272e;
}

.repository-lang-stats-graph .language-color:not(:first-child), .overall-summary, .repository-lang-stats-graph {
    border-color: #1e272e;
}

.commit-tease, table.files td, .file-wrap, .Box-header, .markdown-body h1, .markdown-body h2, .Box-body {
    border-color: #1e272e;
}

.gh-header-meta, .timeline-comment, .Box, .border-bottom, .form-control, .form-select, .timeline-comment-wrapper, .drag-and-drop, .discussion-sidebar-item+.discussion-sidebar-item, .TimelineItem-break, .discussion-timeline-actions, .previewable-comment-form .comment-form-head.tabnav, .timeline-comment-header {
    border-color: #1e272e;
}

.blob-code-deletion * {
    color: #ff5e57 !important;
}

.blob-code-deletion .x {
    background-color: #ff3f34 !important;
    color: white !important;
}

.blob-code-addition * {
    color: #0be881 !important;
}

body, .session-authentication .header-logged-out {
    background-color: black !important;
    color: white !important;
}

.blob-code-addition .x {
    background-color: #05c46b !important;
    color: white !important;
}
}
