css:
@-moz-document domain("www.zdnet.fr") {
  #leaderTop /* Publicité en haut de page */
, #leaderBottom /* Publicité en bas de page */
, .breakingNews /* Bandeau breaking news en haut de page*/
, #page #secondary /* Barre latérale droite encombrante */
, article iframe /* Des iframes dans l'article (notamment le bouton twitter en haut d'article) */
, .sharebar-outer-vertical /* Barre d'outils sur la gauche */
, #main .module /* Après l'arcticle, les sections "à voir aussi" */
, .catfishBar /* La barre de bouton en bas d'article (et qui suit le scrolling) */
{ display: none;}

#page #main {
    float: none;
    display: block;
    margin: auto;
    width: 600px;
}

#page #content {
    width: 700px;
}
}
