css:
@-moz-document domain("stackoverflow.com") {
#content, .top-bar._fixed, .top-bar._fixed *, body {
    background-color: black;
    color: #d2dae2 !important;
}

body * {
    color: #d2dae2 !important;
}

.fc-black-500, .h\:fc-black-500:hover, .f\:fc-black-500:focus {
    color: #d2dae2 !important;
}

code, .s-sidebarwidget__yellow, pre, .mdhelp-tabs {
    background-color: #1e272e;
}

.show-votes .sidebar-linked .spacer>a:first-child .answer-votes, .show-votes .sidebar-related .spacer>a:first-child .answer-votes {
    background-color: #1e272e;
}

.container, #left-sidebar, #left-sidebar *, .s-card, .s-card *, .s-sidebarwidget--header, input, textarea, .grippie, .tag-editor, .s-label {
    background-color: black;
    color: white !important;
}

.sidebar-profile .-header .-subtitle, .sidebar-profile .-footer .-subtitle {
    color: white !important;
}

.-img, .-img:hover {
    opacity: 0;
}

.top-bar .searchbar .s-input, .topbar-dialog, #tabs a, .tabs a, .bg-black-025, .timeline .feature-banner__background, .timeline .feature-banner__background *, .timeline-item.add, .timeline-item.add * {
    background-color: black;
    color: white;
    border: none;
}

.bg-black-025, .h\:bg-black-025:hover, .f\:bg-black-025:focus, .sidebar-profile.is-visible, .tagged-interesting {
    background-color: black;
    color: white;
}

.top-bar .-marketing-link:hover, .top-bar .-marketing-link:focus {
    background-color: #485460;
    color: white;
}

.bg-white, .h\:bg-white:hover, .f\:bg-white:focus {
    background-color: black !important;
    color: white !important;
}

.p-bg-purple-light, .bg-powder-100, .h\:bg-powder-100:hover, .f\:bg-powder-100:focus {
    background-color: black !important;
    color: white !important;
}

.bg-black-050, .h\:bg-black-050:hover, .f\:bg-black-050:focus {
    background-color: black !important;
    color: white !important;
}

.bg-black-025, .h\:bg-black-025:hover, .f\:bg-black-025:focus {
    background-color: black !important;
    color: white !important;
}

.s-modal--dialog, .s-modal--dialog *  {
    background-color: black !important;
    color: white !important;
}

.ps-absolute {
    visibility: hidden;
}

.bg-transparent, .bg-transparent *, .wmd-help-button.active-help, .s-sidebarwidget__yellow .s-sidebarwidget--header {
    background-color: black !important;
    color: white !important;
}

.question-summary, #content, .flush-left, .s-btn__muted.s-btn__outlined.is-selected, .s-btn__muted.s-btn__outlined , .subheader, .bc-black-2, .question-page #answers .answer, .grippie, .s-input, .s-textarea, .wmd-button-row, .wmd-button-bar, .mdhelp, .s-sidebarwidget__yellow .s-sidebarwidget--header, .s-sidebarwidget__yellow .s-sidebarwidget--content, .s-sidebarwidget, .s-card, .s-popover {
    border-color: #1e272e !important
}

.top-bar .my-profile .gravatar-wrapper-24 .-avatar {
    border-radius: 20px;
}

.timeline-item, .timeline-item * {
    color: #d2dae2 !important;
}

a {
    color: #00d8d6;
}

.post-tag, .geo-tag, .container .chosen-choices .search-choice, .container .chosen-container-multi .chosen-choices li.search-choice {
    background-color: #0fbcf9 !important;
    color: white !important;
    border: none;
    
}

.question-status, blockquote {
    background-color: black !important;
    color: white !important;
}

input[type="text"]:not(.s-input), input[type="password"]:not(.s-input), input[type="number"]:not(.s-input), input[type="email"]:not(.s-input), input[type="url"]:not(.s-input), input[type="search"]:not(.s-input), input[type="tel"]:not(.s-input), input[type="datetime"]:not(.s-input), input[type="datetime-local"]:not(.s-input), input[type="date"]:not(.s-input), textarea:not(.s-input) {
    background-color: black !important;
    color: white !important;
    border-color: #1e272e !important
}
}
