css:
@-moz-document domain("gatry.com"){
a, nav ul li a.active {
    color:#898989;
}
header, nav, .content, article, footer, .carregar-mais, .fancybox-skin, .comentarios {
    background-image: url(data:image/gif;base64,R0lGODlhBAAEAKECABERESQkJP///////yH5BAEKAAIALAAAAAAEAAQAAAIFhB6nhlIAOw==) !important;
    background-repeat: repeat !important;
    background-color: #181818 !important;
}
.comentarios ul li {
    border-width: 0px 0px 0px !important;
    color:#FFF;
}
.comment-main{
    border-bottom:1px dashed #595959 !important;
}
.comentarios ul ul {
    margin-left: 10px;
    border-left: 5px dotted #595959 !important;
}
header {
    border-bottom: 0px !important;
}
nav {
    border-bottom: 1px solid #343434;
}
article, .carregar-mais {
    border: 1px solid #343434;
}
header .compartilhar {
    border-left: 0px;
    border-right: 0px;
}
.criar-alerta-home {
    color:#000;
}
input, textarea {
    background-image: url(data:image/gif;base64,R0lGODlhBAAEAKECABERESQkJP///////yH5BAEKAAIALAAAAAAEAAQAAAIFhB6nhlIAOw==) !important;
    background-repeat: repeat !important;
    background-color: #181818 !important;
}
.form-search, .fancybox-inner .comentarios .form-resposta textarea {
    border:1px solid #343434;
}
.promocoes .informacoes .opcoes a.link_loja, .promocoes .informacoes .opcoes a.curtidas, .promocoes .informacoes .opcoes a.mais, .promocoes .informacoes .opcoes a.mais:hover, .fancybox-inner .comentarios ul li a.curtidas, .promocoes .comentarios ul li a.curtidas {
    border: 1px solid #343434;
}
header .compartilhar .btn-compartilhar {
    background:url(https://img.icons8.com/nolan/2x/share.png) no-repeat center center;
    background-size: 50px 50px;
    height: 80px;
    width: 80px;
    text-indent: -9999px;
    margin: 0 20px;
}
header a#link-entrar {
    background:url(https://drive.oceandrop.com.br/assets/img/icones/login-desktop.png) no-repeat center center;
    background-size: 50px 50px;
    height: 80px;
    width: 80px;
    text-indent: -9999px;
    margin: 0 20px;
}
header a {
    color: #573792;
}
}
