css:
@-moz-document url-prefix("https://sisref.sigepe.gov.br/sisref/") {
/*
SISREF Versão 2.0.0.48d (2020-03-04 04:30:00)
*/
html body header nav.navbar-app.navbar-default.navbar-fixed-top div.row div.col-sm-2:first-child{
    display:none;
}
body{
    margin-bottom: auto !important;
}
.btn{
    display: block;
}
body, .comparecimento, .modal-content, .form-group, .form-control {
    background-image: url("data:image/gif;base64,R0lGODlhBAAEAKECABERESQkJP///////yH5BAEKAAIALAAAAAAEAAQAAAIFhB6nhlIAOw==") !important;
    background-repeat: repeat !important;
    background-color: #181818 !important;
    color: #fafafa;
}
div#mensagem_do_sistema{
    width:100% !important;
}
.hora, h5 {
    color: #0c691c !important;
}
.modal-header {
    border-bottom:0px;
}
.modal-footer {
    border-top:0px;
}
.label-sigac {
    color:#FFF;
}
.botao-sigac {
    text-align: center;
    margin:10% auto;
}
.table-striped, .tab {
    background-color: #151515 !important;
}
.table-striped > tbody > tr:nth-of-type(2n+1), .tab button.active {
    background-color: #212121 !important;
}
legend {
    color:#fafafa;
}
fieldset {
    border:0px !important;
}
.row .subtitle {
    border-bottom:1px solid #505050;
}
.table-hover > tbody > tr:hover > td{
    background-color: #0c691c !important;
}
table tbody tr td a{
    color: lightgreen;
}
.footer .container{
    width:100%;
}
.navbar-app{
    background-color:#0c691c;
    color:#FFF;
    position: fixed top;
    float:right;
}
footer.fixed-bottom-x{
    position: relative !important;
    height: auto !important;
}
/*uma barra verde clara no topo que nao faz nada*/
 #barra_div {
    display:none;
}
/*
correção do layout para exibição em telas com resolução até 768px
*/
 @media(max-width: 768px) {
    .container {
        width:95%;
        clear:both;
        position:sticky !important;
    }
    html body div.container .row:first-child {
        margin-top:10px;
    }
    .navbar-toggle {
        display:none
    }
    .botao-sigac{
        width:100%;
        margin-left:auto !important;
        margin-right:auto !important;
        text-align: center !important;
    }
    .botao-sigac a button{
        margin: 0 auto;
        width: 70% !important;
        display:block !important;
    }
    .col-md-12 {
        display: flex;
        flex-direction: column;
        padding-left:0px;
        padding-right:0px;
    }
    .col-md-4 {
        order: 3;
    }
    .col-md-5 {
        order: 1;
    }
    .col-md-6 {
        order: 2;
    }
    .col-xs-6 {
        width:100%;
    }
    .container {
        display: flex;
        flex-direction: column;
    }
    div#registros {
        order: 1;
    }
    div.row.margin-10 {
        order:2;
    }
    div.row#botoes-iteracao {
        order:3;
    }
footer p::after {
  content: " - CSS by: @GuHRM";
}
}
}
