css:
@-moz-document url-prefix("https://sisref.sigepe.gov.br/sisref/") {
/*
SISREF Vers√£o 2.0.0.48d (2020-03-04 04:30:00)
*/
@media (max-width: 768px){
html body header nav.navbar-app.navbar-default.navbar-fixed-top div.row div.col-sm-2:first-child{
    display:none;
}
.container{
    width:95%;
    clear:both;
    padding-left:0px !important;
    padding-right:0px !important;
    position:sticky !important;
}
html body div.container .row:first-child{
    margin-top:10px;
}
.btn{
    display: block;
}
.navbar-toggle{
    display:none
}
.botao-sigac{
    width:100%;
    margin-left:auto !important;
    margin-right:auto !important;
    text-align: center !important;
}
.botao-sigac a button{
    margin: 0 auto;
    width: 70% !important;
    display:block !important;
}
.col-md-12{
    display: flex;
    flex-direction: column;  
    padding-left:0px !important;
    padding-right:0px !important;
}
.col-md-4{
    order: 3;
}
.col-md-5{
    order: 1;
}
.col-md-6{
    order: 2;
}
.col-xs-6{
    width:100%;
}
.container{
    display: flex; flex-direction: column;  
}
div#registros{
    order: 1;  
}
div.row.margin-10{
	order:2;
}
div.row#botoes-iteracao{
	order:3;
}
.row{
    margin-right: 0px !important;
	margin-left: 0px !important;
}
footer{
    height:auto !important;
}
footer p::after {
  content: " - CSS by: @GuHRM";
}
}
}
