css:
@-moz-document domain("downloadha.com") {
body {
      line-height: 1;
}
.last-movies {
      display: none;
}
.container-fluid {
      max-width: 75%;
      min-width: 900px;
}
.post-tags,
.fixed-posts,
.ads-no-margin,
.top-posts-ads,
.footer-links,
.popup-ads.col-xs-12,
.tab-box,
.tab-pane,
.top-posts-ads,
.logo-wrapper
{
      display: none;
}
.col-sm-8 {
      width: auto;
}
popup-ads {
      display: none;
}
.cat-icon {
      margin: 0px;
}
.col-sm-8 {
      width: auto;
      column-count: 2;
}
.panel {
      max-width: 100%;
      margin-left: 8px;
      overflow-y: auto;
      max-height: 100%;
}
.panel a {
      line-height: 1.5;
      font-size: 11pt;
}
.entry-content {
      clear: both;
      overflow: auto;
      font-size: 10pt;
      line-height: 1.5;
}
aside {
      display: none;
}
.ads {
      display: none;
}
.btn {
      padding: 10pt 60pt;
      font-size: 10pt;
}
.download-box {
      position: fixed;
      bottom: 32pt;
      top: 120px;
      left: 8px;
      width: 23%;
      max-width: 500px;
      min-width: 400px;
      z-index: 100;
      overflow: auto;
      font-size: medium;
      line-height: 2;
}
.pagination {
      display: inline-block;
      padding-left: 0;
      margin: 50px;
      position: fixed;
      bottom: 0px;
      left: 0px;
      box-shadow: -4px 4px 4px 0px rgba(0, 0, 0, .18);
      transition-duration: 0.4s;
      border-radius: 8px;
}
.pagination > li > a, .pagination > li > span
{
      transition-duration: 0.4s;
}
.download-box .btn-groups {
      padding-bottom: 0px;
}
.btn.btn-danger {
      padding: 1pt 1pt;
}
.comments, .linkbox {
      width: 485px;
      font-size: 10pt;
      line-height: 1.4;
}
.linkbox a {
      font-size: 9pt;
}

.form-group,
.label-floating,
.is-empty {
      display: contents;
}
}
