css:
@-moz-document domain("manganelo.com")
{
/*REMOVE ADS*/
/* iframe, */
body > a[href],
.mb10,
.tips > div[style],
.c,
#shares,
#advertisment,
#rss,
.container-footer-content,
.advertisment,
/* #spotlights, */
#intro > span,
.left > div:nth-child(4),
#mfad600x90,
#ad_top,
.ad,
#ad,
#intro,
#top.topless,
.fb-page.fb_iframe_widget,
.stcpDiv,
/* #fb-root, */
.tab_comment.clearfix li:first-child,
.fb-page,
[id^=ad],
[class^=ad],
[id^=fb],
[id^=Market],
[id^=M301863],
.footer-content,
div.middleCol > div:nth-child(1) > div{
    display: none!important;
}

/*/////////// FONT /////////////*/

li span a,
.sts,
.bookmark_title,
.bookmark_chap>a,
.history_title,
.history_chap>a,
body > div.container > div.main-wrapper > div.leftCol > div.daily-update > div > div > div > em > a{
    color: wheat !important;
}
a,
.xem-nhieu-item h3 a,
h3 a,
body > div.container > div.main-wrapper > div.leftCol > div.daily-update > div > div > div > span{
    color: white!important;
}
/* li span a:visited,
span a:visited,
.doreamon a:visited {
    color: crimson !important;
} */

a:hover, a:active {
    color: yellow!important;
}


body > div.container > div.main-wrapper > div.middleCol > div.panel-category > table > tbody > tr > td > a:hover{
    text-shadow: 0.8px 0.8px yellow;
}

body > div.container > div.main-wrapper > div.leftCol.listCol > div > div > h3 > a,
body > div.container > div.main-wrapper > div.leftCol > div.daily-update > div > div > div > h3 > a,
.genres-item-info>*>a{
    color: wheat!important;
    text-decoration: underline;
}

.bookmark_list .bookmark_item,
.history_list .history_item{
    color: #fff !important;
}

.genres-item-info>*,
.aye_icon,
.panel-story-chapter-list>ul>*{
    color:white!important;
}

.genres-item-description-linear,
.genres-item-description-linear::after{
    background: none!important;
    border-bottom: unset!important;
    content: none!important;
}




/*////////// BACKGROUND /////////////*/

/* LAYOUT */

.genres-item-description
{
    margin-top: 40px!important;
    height: 40px!important;
}

.genres-item-readmore{
    background-color: #3F3F48;
    -webkit-transition: all .5s;
    border-radius: 20px;
}

.panel-breadcrumb,
.genres-item-readmore:hover{
    background-color: #3498db;
    -webkit-transition: all .5s;
}

.genres-item-description{
    height: 80px!important;
}

.genres-item-info,
.content-genres-item{
    height:220px!important;
}


#search-story{
    background-color: white;
}

.group-page a{
    background: #059e9a!important;
}

.group-page>a.page-select {
    box-shadow: inset 0 0 0 4px #1b1b1d;
}


.logo a img{
    padding: 7px!important;
    border-top-left-radius: 20px!important;
    background-image: -moz-linear-gradient(bottom right, #3f3f48 15%, #3f3f48 60%);
}

.logo{
    margin-right: 0%!important;
    border-top-left-radius: 20px!important;
}

.menu-item li a:hover {
    text-shadow: 0 8px 6px -6px black;
}

.itemupdate.first,
.content-homepage-item{
    box-shadow: 0 8px 16px 0 rgba(0, 0, 0, 0.2), 0 6px 20px 0 rgba(0, 0, 0, 0.19);
    padding-right: 1.00%;
}

a img {
    background-blend-mode: darken!important;
}
.top-header {
    /*     border-right: 1px white solid;
    border-left: 1px white solid;
    border-top: 1px white solid; */
    width: 80%;
    border-top-right-radius: 20px;
}


.container-header,
.header-menu {
    /*     border-right: 1px white solid;
    border-left: 1px white solid;
    border-bottom: 1px white solid; */
    width: 80%;
}


/* ----------------------------------------- */
.panel-category > table > tbody>*,
.panel-category> *{
    background-color: #3F3F48!important;
}

.panel-category-title{
    float:unset!important;
}
/* ----------------------------------------- */


body,
footer,
#chapter,
/*.comments,*/
.new_update_trangdoc,
.option_wrap {
    background: #1B1B1D;
    background-color: #1B1B1D;
    border-top: unset!important;
}

body > div.comments > div.new_update_trangdoc > div > h4{
    padding-left: 1.1em!important;
}


.container-header,
.container-header-top,
.content-genres-item,
div.panel-genres-list,
div.panel-genres-list li,
.spec-tag li a,
.daily-update-item,
.bookmark_list .bookmark_item,
.history_list .history_item{
    background: #3F3F48!important;
    background-color: #3F3F48!important;
    border-left: #3498db;
}

.container-header,
.header-menu,
.panel-story-chapter-list,
#chapter,
.page,
p.panel-genres-list {
    background: #2A2A2E!important;
    background-color: #2A2A2E!important;
    border-top: unset;
}

body > div.container > div.main-wrapper > div.leftCol > div.daily-update > div{
    background: #3f3f48;
}

.content-homepage-more:hover{
    background:  #3F3F50!important;
    background-color: #3F3F50!important;
    -webkit-transition: all .5s;
}

.content-homepage-more{
    background: #2A2A2E!important;
    background-color: #2A2A2E!important;
    -webkit-transition: all .5s;
}

.panel-content-homepage {
    border-top-right-radius: 10px!important;
    background: #3F3F48!important;
    background-color: #3F3F48!important;
    background-blend-mode: darken!important;
    background-position: top !important;
    background-repeat: no-repeat!important;
}
.tag-title,
.title.all-title,
.silder-title {
    background: #00000080 !important;
    border-spacing: 0!important;
    background-attachment: fixed !important;
    background-position: 50% 0% !important;
    border-top: unset!important;
}

.content-homepage-item:nth-child(4n+4),
.content-homepage-item:nth-child(4n+3)
{
    background-color: #1E1E20!important;
}

.panel-content-homepage .content-homepage-title{
    border-bottom: 2px solid #1E1E20;
    border-bottom-width: 10px;
    margin-bottom: 0px;

}

/* .xem-nhieu-item:nth-of-type(even) {
    background-color: #00000080 !important;    
    background-attachment: fixed !important;
}
.xem-nhieu-item:nth-of-type(odd) {
    background-color: #3F3F48 !important;
    background-attachment: fixed !important;
    background-position: 50% 0!important;
} */

.daily-update,
.content-homepage-title,
.panel-category,
.panel-newest,
.panel-topview,
.container-silder {
    border: unset;
    margin-bottom: 0px;
    padding-bottom: 10px;

}

.panel-category,
.pn-category-row-border{
    border-bottom: 2px solid #3498db!important;
}

.row1,
.post_block.row1 {
    background-color: #00000040 !important;
}
.rowitemupdate {
    display: block!important;
}

}
