css:
@-moz-document domain("webtoons.com"){
:root{
    --lgray:#cbcbcb;
    --mgray:#777;
    --black:#000;
    --dgray:#0b0b0c;
    --vdgray:#2a2a2a;
    --white:#fff;
}
.body,#header{background:var(--dgray)}
html:not(.__lt-dummy-1):not(.__lt-dummy-2):not(.__lt-dummy-3):not(.__lt-dummy-4):not(.__lt-dummy-5):not(.__lt-dummy-6):not(.__lt-dummy-7){background:var(--black)}
#content{background:var(--black)!important}
.daily_tab_wrap{
    background: var(--dgray);
    border-top:0px;
    border-bottom:0px}
.daily_tab li .btn_daily{color:var(--lgray)}
#header{color:var(--lgray)}
a{color:var(--mgray)}
.sta .login {background:var(--dgray);color:var(--lgray);border:1px solid var(--black)}
.btn_search {-webkit-filter: invert(100%);filter: invert(100%);}
.main_daily_wrap {background: var(--black)}
.card-item .card-front{background:var(--dgray)}
.main_hotnew_wrap {
	border-top: 1px solid var(--dgray);
	background: var(--black);}
.main_hotnew h2 {color:var(--lgray)}
.main_genre_wrap {
	background: var(--black);
	border-top: 1px solid var(--dgray);}
.recently_area .menu {
    -webkit-filter: invert(100%);
    filter: invert(100%);}

/*MOST POPULAR*/
.lst_type1 {border-bottom:1px solid var(--vdgray)}
.lst_type1 li {border-top: 1px solid var(--vdgray)}
.lst_type1 .info_area .author {color:var(--mgray)}

/*CANVAS*/
.main_challenge_wrap{border-top:1px solid var(--dgray)}
.main_challenge {background:var(--dgray)}
.main_challenge .title_area {color:var(--lgray)}
.ly_lst_genre{background:var(--dgray);border:1px solid var(--vdgray)}

/*BOTTOM FOOTER*/
#footer{background:var(--black);border-top:1px solid var(--dgray)}
.ico_arr3,.foot_sns{-webkit-filter: invert(100%);filter: invert(100%);}
.foot_app{background:var(--black);border-bottom:1px solid var(--dgray)}
.foot_app .foot_down_msg .txt{color:var(--lgray)}
.notice_area{background:var(--black);border-bottom:1px solid var(--dgray)}
.language .lk_lang,.language .ly_lang {background:var(--black)}

/*COMMENTS*/
.u_cbox,.u_cbox .u_cbox_recomm_set .u_cbox_btn_recomm, .u_cbox .u_cbox_recomm_set .u_cbox_btn_unrecomm {background:var(--dgray)}
.u_cbox_title,.u_cbox .u_cbox_name {color:var(--lgray)}
.u_cbox .u_cbox_write .u_cbox_inbox .u_cbox_text {background-color:var(--vdgray)}
.u_cbox .u_cbox_btn_upload {background:var(--mgray)}
.u_cbox .u_cbox_write .u_cbox_inbox{background-color:var(--vdgray)}
.u_cbox .u_cbox_comment_box,.u_cbox .u_cbox_sort {border-bottom: 1px solid var(--vdgray)}
.u_cbox .u_cbox_paginate .u_cbox_page {color:var(--lgray)}
.u_cbox .u_cbox_paginate .u_cbox_next .u_cbox_ico_page{-webkit-filter: invert(100%);filter: invert(100%)}

/*CREATORS NOTE*/
.comment_area .creator_note {border-bottom: 1px solid var(--vdgray)}
.comment_area .creator_note h2 {color:var(--white)}

/*OTHER EPISODES*/
.episode_area {background:var(--vdgray)}
.episode_lst ul .mask {display:none}

/*BUTTONS AND TXT STRINGS*/
.spi_area .bx,.btn_top,.lst_type1 .ranking_top .pic_area,.lst_type1 .num_ranking,.sta .lk_creators a::before,.search_area{-webkit-filter: invert(100%);filter: invert(100%)}
.viewer_lst .day_info,.ranking_lst.viewer .title_area h2 a, .ranking_lst.viewer .title_area h2 span,.viewer .challenge_spot_inner h3 ,.ranking_lst.top .title_area h2{color:var(--lgray)}
.viewer .challenge_spot {border-top:1px solid var(--dgray)}
.daily_card_item .subj{color:var(--black)}
.daily_head {color:var(--lgray);border-bottom: 1px solid var(--vdgray);}
.lst_type1 .ranking_top a,.detail_body .detail_lst li > a:hover {background:var(--black)}
.ranking_lst .title_area h2 a, .ranking_lst .title_area h2 span {color:var(--lgray)}

/*COMIC LIST PAGE*/
.detail_header .spi_wrap,.ly_creator,.detail_body .detail_lst .subj span {-webkit-filter: invert(100%);filter: invert(100%)}
.detail_other.recommnd h2,.aside.detail .grade_area .cnt,.aside.detail .day_info,.aside.detail .summary {color:var(--lgray)}
.aside.detail {border-left:2px solid var(--vdgray); background:var(--dgray);color:var(--lgray)}
.detail_body .detail_install_app {border-top:1px solid var(--vdgray); background:var(--dgray);color:var(--lgray)}
.detail_body.banner .detail_lst,.detail_body .detail_lst li:hover,.detail_body {background:var(--dgray)}
.detail_body .detail_lst {border-right: 2px solid var(--dgray);}
.detail_body .detail_lst li:first-child {border-top: 1px solid var(--vdgray);}
.detail_body .detail_lst li {border-bottom:1px solid var(--vdgray)}
.paginate a, .paginate.v2 {color:var(--lgray)}
.element {background: var(--black)}
.detail_body .detail_lst {border-right: 2px solid var(--dgray);background: var(--dgray);}

/*ACCOUNT*/
.snb_inner {background:var(--black);color:var(--lgray)}
.snb_wrap {border-top:1px solid var(--vdgray);border-bottom:1px solid var(--vdgray);background:var(--black)}
#wrap.header_bdr #header {border-bottom:1px solid var(--vdgray)}
.card_item .info .subj,.card_item .update {color:#000}
.card_wrap.my .sub_title.v2,.card_wrap.my .sub_title.v3,.card_wrap.my .sub_title.v4,.card_wrap.my .sub_title_wrap,.recently_lst .tit,.snb a{color:var(--lgray)}
.account_wrap {-webkit-filter: invert(100%);filter: invert(100%)}

/*DISCOVER*/
.discover_lst li {border:1px solid var(--vdgray);background:var(--vgray)}
.discover_cont_area .tit {color:var(--lgray)}

/*CREATORS101*/
.canvas_info_wrap .canvas_info,.creator_cont .title {color:var(--lgray)}
.aside_area,.creator_cont .more {-webkit-filter: invert(100%);filter: invert(100%);}
.thumbnail_lst li {background:var(--black);color:var(--lgray);border:1px solid var(--vdgray)}
.creator_hub_wrap.money {color:var(--lgray)}

/*YOUMAYALSOLIKE*/
.detail_other .lst_type1 li {background: var(--black)}
.detail_other h2{color:var(--white)}

/*RECENTS*/
.recently_area{background:#000000de}
.recently_cont .subj,.recently_area .t_recently2{color:var(--white)}
.recently_cont .bar{background:var(--vdgray)}
.recently_area.unfd [class$="_line"]{border-left: 1px solid var(--vdgray)}

/*PUBLISH*/
.publish_help .subj,.publish_title{color:var(--white)}
.select1{-webkit-filter: invert(100%);filter: invert(100%)}
.txtarea,.input_txt{border: 1px solid var(--vdgray) !important;background:var(--black)}
.publish_form_dsc3{color:var(--white)}
.ico_chkbox,.ico_chkbox.on{background-color:var(--dgray)}

/*LOGIN_FIX*/
.ly_loginbox .btn_clse2,.age_gate_area .form_area .input_text{filter:invert()}
.ly_loginbox.bg_wh, .loginbox2{background:var(--dgray)}
#container.bg,.cont_box2 .inner_wrap{background:var(--black)}
.ly_loginbox.v2.bg_wh .bg_line .line_txt,.loginbox2 .bg_line .line_txt{background:var(--dgray);color:white}
.ly_loginbox.v2.bg_wh .input_box, .loginbox2 .loginbox_cont .t_login,.loginbox2 .input_box,.ly_loginbox.bg_wh .loginbox_cont .t_login,.reset_pw_area strong,.cont_box2 .input_box{color:white}
.ly_loginbox.v2.bg_wh .input_box,.loginbox2 .input_box{border-bottom: 1px solid var(--mgray)}
.loginbox2 .bg_line{background:var(--mgray)}
.sta .lk_login{border: 1px solid var(--vdgray);background: var(--vdgray);color:var(--white)}
.ico_resetpw{display:none}

/*GDPR&AGE AGREEMENTS*/
.gdpr_ly_cookie.on{background:black;}
.gdpr_ly_cookie .dsc{color:white}
.gdpr_ly_cookie .btn_clse2{filter:invert()}
.ly_box{background:black}
.chk_area{filter:invert()}


}
